import * as api from '@/api/project/ems/energyReport/energyCostsReport.js'
import Pie from '@/views/project/EMS/energyReport/temp/pie.vue'
import { mapState } from 'vuex'
export default {
    name: 'keyReport', //重点产品报表
    components: { Pie },
    data () {
        return {
            theight: 0, //表格高度
            list: [],
            dataTime: undefined,
            listQuery: {
                energyConsumptionLevel: undefined, //level_step
                startTime: undefined
            },
            xAxis: null,
            chartData: []
        }
    },
    computed: {
        ...mapState({
            dt_energyConsumption_level: state =>
                state.emsDict.dt_energyConsumption_level
        }),
        holder () {
            return this.list.length % 3 == 0 ? 0 : 3 - (this.list.length % 3)
        }
    },
    mounted () {
        this.$store.dispatch('emsDict/getDicData', ['dt_energyConsumption_level'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    destroyed () {
        this.$store.dispatch('emsDict/getDicData', ['dt_energyConsumption_level'])
        this.setTableHeight()
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.query(this.listQuery).then(response => {
                this.list = response.data
                this.xAxis = response.data.map(res => {
                    return res.levelCode
                })
                let chartData = []
                response.data.forEach((i, index) => {
                    let s = [
                        { value: i.waterCost, name: '水' },
                        { value: i.eleCost, name: '电' },
                        { value: i.gasCost, name: '气' }
                    ]
                    chartData.push({
                        id: `pie-costs-${index}`,
                        name: i.levelCode,
                        data: s
                    })
                })
                this.chartData = chartData
            })
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.energyConsumptionLevel = undefined  //level_step
            this.listQuery.startTime = undefined
            this.getList()
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        }
    }
}
