result:
$minColor: skyblue;
$minPadding: 15px;
$BBC: 1px solid #ededed;
$red: red;
$blue: blue;
$gray: gray;
$orange: orange;
$green: green;
.fault {
    background-color: $red;
}
.standby {
    background-color: $orange;
}
.run {
    background-color: $green;
}
.offline {
    background-color: $gray;
}
.breakdown {
    background-color: $blue;
}
.line-box {
    background-color: #fff;
    .lin-title {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        position: relative;
        padding: $minPadding;
        border-bottom: $BBC;
        font-size: 12px;
        .item {
            display: flex;
            justify-content: flex-start;
            align-items: center;
            span {
                width: 8px;
                height: 8px;
                border-radius: 50%;
                margin: 0 5px;
            }
            p {
                flex: 1;
            }
        }
    }
    .line-contern {
        padding: $minPadding;
        .content-box {
            display: flex;
            justify-content: space-between;
            flex-wrap: wrap;
            border-bottom: $BBC;
            padding: $minPadding;
            padding-bottom: 0;
            .holder {
                opacity: 0;
            }
            .content-box-item {
                width: 15%;
                background-color: #2d2d2d;
                border-radius: 5px;
                margin-bottom: $minPadding;
                padding: 10px;
                color: #fff;
                p {
                    font-size: 12px;
                    text-align: center;
                    &:first-child {
                        font-size: 14px;
                        font-weight: 600;
                    }
                    &:last-child {
                        color: #ff6600;
                    }
                }
                .eng-body {
                    display: flex;
                    justify-content: space-between;
                    span {
                        text-align: center;
                        padding: 5px 0;
                    }
                    span:nth-of-type(2n-1) {
                        flex: 1;
                        background-color: $minColor;
                        color: #fff;
                    }
                    span:nth-of-type(2n) {
                        flex: 2;
                        background-color: #fff;
                    }
                }
                .item {
                    display: flex;
                    justify-content: flex-start;
                    align-items: center;
                    p {
                        flex: 1;
                        display: flex;
                        align-items: center;
                        span {
                            width: 8px;
                            height: 8px;
                            border-radius: 50%;
                            margin: 0 5px;
                        }
                    }
                }
            }
        }
        .eng-footer {
            p {
                text-align: center;
                cursor: pointer;
            }
        }
    }
}
