import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { mapState } from "vuex"

export default {
    data () {
        return {
            dialogStatus: '',
            dialogVisible: false,
            temp: {
                classify: 'mould'
            },
        }
    },
    computed: {
        ...mapState({
            dt_srm_paz: state => state.dict.dt_srm_paz,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_paz"])
    },
    methods: {
        create () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecordsbqd(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('getFreshList')
                    })
                } else {
                    return false
                }
            })

        },
        update () {
            api.updateRecordsbqd(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        },
        resetTemp () {
            this.temp = {
                name: undefined,
                code: undefined,
                function: undefined,
                category: undefined,
                quantity: undefined,
                manufacturer: undefined,
                picture: undefined,
                supplierId: this.temp.supplierId,
                classify: 'mould'
            }
        }
    }
}
