import * as api from "@/api/project/srm/fileUpload/upload"
export default {
    props: ['functionCode', 'fileType'],
    data () {
        return {
            dialogVisible: false,
            list: [],
            files: [],
            fileList: [],
            filename: ''
        }
    },
    methods: {
        getList () {
            let dto = {
                currentPage: 1,
                pageSize: 10,
                functionCode: this.functionCode,
                fileType: this.fileType
            }
            api.queryRecords(dto).then(res => {
                console.log(res, 'hhhhh')
                this.list = res.data.list
            })
        },
        handleClose () {

        },
        handleDelete (index, row) {
            console.log(index, row)
            let dto = {
                fileCode: row.fileCode
            }
            api.deleteRecord(dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getList()
            })
        },
        fileLimit (file) {
            const isLt10M = file.size / 1024 / 1024 < 10
            if (!isLt10M) {
                this.$message({
                    message: '上传文件大小不能超过 10MB!',
                    type: 'warning'
                })
            }
            return isLt10M
        },
        uploadFile () { },
        fileExceed () {
            this.$message.error('一次最多上传10个文件')
        },
        handleFile (file, fileList) {
            this.files = fileList
            console.log(this.files, ' this.files')
        },
        handleUpload () {
            console.log(this.functionCode, this.fileType, 'fileType')
            if (!this.files.length) {
                this.$message({
                    title: '警告',
                    message: '请选择文件',
                    type: 'warning'
                })
                return false
            }
            var data = new FormData()
            this.files.forEach(file => {
                data.append('files', file.raw)
            })
            let dto = {
                // functionCode: this.functionCode,
                fileType: this.fileType,
                category: 'srm'
            }
            console.log(data, 'datadata')
            api.upload(this.functionCode, data, dto).then(res => {
                console.log(res, 'kkkkk')
                if (res.status == 200) {
                    this.$message({
                        title: '成功',
                        message: '上传成功',
                        type: 'success',
                        duration: 2000
                    })
                }
                this.getList()
                this.files = []
                this.fileList = []
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        handleDownload (index, row) {
            window.open(`http://localhost:7954/basic/files/download?readName=${row.readName}`)
        }
    },
}
