//首页样式
.dashboard-container {
    margin: 15px !important;
    ul,
    li {
        padding: 0;
        margin: 0;
        list-style: none;
        padding-left: 10px;
        font-size: 12px;
    }

    li {
        height: 30px;
    }

    .time {
        position: absolute;
        right: 20px;
    }

    .head-portrait {
        border: solid 1px #d5d8da;
        border-top: solid 6px #d5d8da;
        background-color: white;
        height: 200px;
    }

    .title {
        border-bottom: solid 1px #d5d8da;
        padding-left: 20px;
        line-height: 36px;
        font-size: 14px;
    }

    li a {
        max-width: 245px;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
        display: inline-block;
        height: 30px;
        line-height: 30px;
    }
}

.dashboard-container .el-row {
    margin-bottom: 20px;
    &:last-child {
        margin-bottom: 0;
    }
}

.infopic {
    height: 100px;
    background-color: #aabbcc;
}

.dashdiv {
    background-color: #fff;
    border-top: solid 6px #d5d8da;
}

.spanblock {
    border-top: 1px solid #aabbcc;
    line-height: 25px;
    margin-top: 5px;
}

.spanblock span {
    display: block;
}

.textdiv {
    width: 100%;
    height: 100px;
}

.textdiv span {
    display: block;
    text-align: center;
    position: relative;
    top: 35%;
}

.iconstyle {
    color: brown;
    font-size: 30px;
}

.info_title {
    font-size: 15px !important;
}

.info_num {
    font-size: 28px !important;
    color: #7ecef4 !important;
}

.padding20 {
    padding: 20px;
}

.dash_ul {
    li span {
        display: inline-block;
    }

    .left {
        float: left;
    }
    .right {
        float: right;
    }

    .red {
        background-color: red;
        width: 20px;
        height: 10px;
        margin-top: 8px;
    }
    .yellow {
        background-color: yellow;
        width: 20px;
        height: 10px;
        margin-top: 8px;
    }
    .orange {
        background-color: orange;
        width: 20px;
        height: 10px;
        margin-top: 8px;
    }
}
