import Pagination from '@/components/Pagination' // 分页组件
import * as api from '@/api/project/wms/purchaseReturn/undirectedReturn'
import { mapState } from 'vuex'
import AuthoType from '@/components/Select/AuthoType.vue'//权限类型下拉组件
import gysxz from '@/components/Select/gysxz'//供应商选择
import qrcode from "@/components/QRCode"
import wlxzbykcz from '@/components/Select/wlxzbykcz'//库存帐物料信息模块


export default {
    name: "undirectedReturn",
    components: { Pagination, qrcode, wlxzbykcz, AuthoType, gysxz },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            gysMutiple: true,
            gysValue: '',
            isGysRequired: false,
            options: [],
            options1: [],
            areaList: null,
            locatorList: [],
            list: [],
            parentsRows: [],
            list1: null,
            list2: null,
            total: 0,
            total1: 0,
            total2: 0,
            activeName2: 'first',
            unMutiple: false,
            isEdit: false,
            disabled: false,
            createTime: [this.currentTime(), this.currentTime()],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: undefined,
                arrivalOrgCode: '',
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: this.currentTime(),
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                arrivalOrgCode: '',
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: undefined,

                proofNo: undefined,
            },
            dto: {
                accountDate: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                orgName: undefined,
                outOrderPlans: [],
                sourceOrderCode: undefined,
                warehouseCode: undefined,
            },
            locationCode: undefined,
            proofNo: undefined,
            warehouseCode: undefined,
            orgCode: undefined,
            listQuery2: {
                proofNo: undefined,
                currentPage: 1,
                pageSize: 10,
            },
            printData: {},//打印数据 
            value1: '',
            dialogStatus: '',
            dialogPrintVisible: false,
            gysxzDialogFormVisible: false,
            gysxzDialogFormVisible1: false,
            wlxzDialogFormVisible: false,
            tableKey: 0
        }
    },
    computed: {
        ...mapState({
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
            dt_uninstructed_return: state => state.businessComponent.dt_uninstructed_return
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('dict/getDicData', ['dt_materiel_type', 'dt_materiel_classify'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', ['dt_uninstructed_return'])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 30
            this.pheight = this.$myFun.getSingleTbHeight() + 80
            this.sheight = this.$myFun.getSingleTbHeight()
        },
        currentTime () {
            var date = new Date()
            var seperator1 = "-"
            var year = date.getFullYear()
            var month = date.getMonth() + 1
            var strDate = date.getDate()
            if (month >= 1 && month <= 9) {
                month = "0" + month
            }
            if (strDate >= 0 && strDate <= 9) {
                strDate = "0" + strDate
            }
            var currentdate = year + seperator1 + month + seperator1 + strDate
            return currentdate
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_uninstructed_return.options[0].wareRuleCode
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQuery1.orgCode = val
            } else if (i === 3) {
                this.listQuery1.warehouseCode = val
                this.listQuery1.businessType = this.dt_uninstructed_return.options[0].wareRuleCode
            }
        },
        changeOrg () {//组织编码下拉change
            this.listQuery1.orgCode = this.listQuery.orgCode
        },
        changeToWa () {//仓库编码下拉change
            this.listQuery1.warehouseCode = this.listQuery.warehouseCode
        },
        changeBusinessType () {//业务类型下拉change
            this.listQuery1.dicType = this.listQuery.businessType
        },

        getList1 () {//查询凭证号
            // const createTime = this.listQuery1.createTime
            if (this.createTime) {
                this.listQuery1.createDateStart = this.createTime[0]
                this.listQuery1.createDateEnd = this.createTime[1]
                // this.listQuery1.createTime = undefined
            } else {
                this.listQuery1.createDateStart = ""
                this.listQuery1.createDateEnd = ""

            }
            api.query(this.listQuery1).then(res => {
                this.list1 = res.data.list
            })
        },
        getList2 () {//已选项查询
            api.queryDetails(this.listQuery1, this.selectlistRowchange.outOrderNumber).then(res => {
                this.list2 = res.data.list
                this.total1 = res.data.pages.count
            })
        },
        selectRow1 (val) {
            this.selectRowData1 = val
        },
        showGys (val) {
            this.gysValue = val
            this.$refs.dialogRefgys.dialogVisible = true
        },
        //供应商确认
        handleGysClick (gysRows) {
            if (this.gysValue === 'register') {
                this.parentsRows = gysRows
                if (!this.parentsRows[0]) {
                    this.$set(this.listQuery, 'supplierCode', "")
                } else {
                    this.$set(this.listQuery, 'supplierCode', this.parentsRows[0].supplierCode)
                }
            } else if (this.gysValue === 'proof') {
                this.parentsRows = gysRows
                if (!this.parentsRows[0]) {
                    this.$set(this.listQuery1, 'supplierCode', "")
                } else {
                    this.$set(this.listQuery1, 'supplierCode', this.parentsRows[0].supplierCode)
                }
            }
        },
        handleUpdate () {
            let result = true
            let groupList = []
            this.dto.orgCode = this.listQuery.orgCode
            this.dto.orgName = "组织名称"
            this.dto.businessType = this.listQuery.businessType
            this.dto.customerCode = this.listQuery.supplierCode
            this.dto.accountDate = this.listQuery.accountDate
            this.dto.warehouseCode = this.listQuery.warehouseCode
            this.dto.sourceOrderCode = "PO20190524200"

            this.list.forEach((item) => {

                this.list.map(v => {
                    v.outStockNum = v.moveNum
                    v.wareAreaCode = v.areaCode,
                        v.wareLocationCode = v.locationCode
                    v.sourceDetailNo = v.rowId
                    return v
                })

                if (item.outStockNum > 0) {
                    groupList.push(item)
                    result = false
                    this.dto.outOrderPlans = groupList
                    if (result) {
                        this.$message({
                            title: this.$t("message.warning"),
                            message: this.$t("message.deliveryCannotBeZeroOrEmpty"),
                            type: "warning"
                        })
                    }
                }
            })

            this.dto.outOrderPlans = groupList
            api.addRecords(this.dto).then(() => {
                this.disabled = true
                this.$message({
                    title: this.$t("message.success"),
                    message: this.$t("message.saveSuccessfully"),
                    type: 'success',
                    duration: 2000
                })
            })
        },
        handleCreate () {//新增弹窗
            if (this.listQuery.orgCode && this.listQuery.businessType && this.listQuery.accountDate && this.listQuery.warehouseCode && this.listQuery.supplierCode) {
                this.wlxzDialogFormVisible = true
                this.orgCode = this.listQuery.orgCode
                this.warehouseCode = this.listQuery.warehouseCode
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.list]
                    this.$refs.wlxzRef.isEdit = true
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.selectQueryConditions"),
                    type: "warning"
                })
            }
        },
        update () {
            if (this.$refs.wlxzRef.listSelected.length) {
                this.$refs.wlxzRef.listSelected.map(v => {
                    v.areaCode = v.wareAreaCode
                    v.locationCode = v.wareLocationCode
                    v.measuringUnit = v.unitName
                    v.specification = v.unit
                    return v
                })

                this.list = this.$refs.wlxzRef.listSelected

                this.wlxzDialogFormVisible = false
                this.isEdit = true
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedoneplease"),
                    type: "warning"
                })
            }
        },
        handleDelete () {//删除列表
            if (this.selectRowData1 && this.selectRowData1.length == 1) {
                this.$confirm(this.$t("message.isDeleteContinue"), this.$t("message.prompt"), {
                    confirmButtonText: this.$t("message.confirm"),
                    cancelButtonText: this.$t("message.cancel"),
                    type: 'warning',
                }).then(() => {
                    for (let i = 0; i < this.list.length; i++) {
                        if (this.list[i].materielCode === this.selectRowData1[0].materielCode) {
                            this.list.splice(i, 1)
                            this.$message({
                                title: this.$t("message.success"),
                                message: this.$t("message.deleteSuccessed"),
                                type: 'success',
                                duration: 2000
                            })
                        }
                    }
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: this.$t("message.deleteCanceled")
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedoneplease"),
                    type: 'warning'
                })
            }
        },
        handelRefresh () {//刷新
            this.isEdit = false
            this.disabled = false
            this.list = []
            // this.listQuery=[]
        },
        // handelRefresh1(){
        //     this.listQuery=[]
        // },
        handleFilter1 () {
            this.getList1()
        },
        //导出
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '业务类型', '物料编码', '物料名称',
                    '数量', '供应商编码', '供应商名称', '组织编码',
                    '仓库编码', '区位', '储位', '记账日期',
                    '创建日期', '记账人', '订单号', '订单行号',
                ]
                const filterVal = [
                    'outOrderNumber', 'businessType', 'materielCode', 'materielName',
                    'outStockNum', 'customerCode', 'customerName', 'orgCode',
                    'warehouseCode', 'wareAreaCode', 'wareLocationCode', 'accountDate',
                    'createTime', 'creator', 'sourceOrderCode', 'rowNo',
                ]
                const data = this.formatJson(filterVal, this.list2)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))

        },
        selectRowchange (val) {
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
            this.$refs.tbb.setCurrentRow(this.selectlistRowchange)
            if (this.selectlistRowchange) {
                this.getList2()
            }
        },
        clickProofNo (val) {
            this.clicklistProofNo = val
            this.$refs.tbb.clearSelection()
            this.$refs.tbb.toggleRowSelection(val) //单击行绑定点击事件
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowchange) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowchange
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })
            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }
        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)

        },
    }
};
