import { queryRecords } from "@/api/project/srm/dataBase/dictMaintenance"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import addMain from "./dialog/addMain"
import { mapState } from "vuex"

export default {
    name: "dailyEvaluate",
    components: { Pagination, caiGou, addMain },
    data () {
        return {
            phaseList: [],
            modalnum: null,//模板编号，非模板页面可删
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0,//表格索引
            tableConfigSub: [
                { label: '状态', prop: 'state', align: 'center', width: 'auto' },
                { label: '评价类型', prop: 'evaluationType', align: 'center', width: 'auto' },
                { label: '评价阶段', prop: 'evaluationPhase', align: 'center', width: 'auto' },
                { label: '年度', prop: 'year', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierCode', align: 'center', width: 'auto' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: 'auto' },
                { label: '评价类', prop: 'evaluateClass', align: 'center', width: 'auto' },
                { label: '得分', prop: 'score', align: 'center', width: 'auto' },
                { label: '等级', prop: 'rank', align: 'center', width: 'auto' },
                { label: '评价', prop: 'evaluate', align: 'center', width: 'auto' },
                { label: '评价人', prop: 'evaluationPerson', align: 'center', width: 'auto' },
                { label: '提交日期', prop: 'submitDate', align: 'center', width: 'auto' },
                { label: '组织编码', prop: 'orgCode', align: 'center', width: 'auto' },
                { label: '评价单号', prop: 'evaluationCode', align: 'center', width: 'auto' },
                { label: '行号', prop: 'rowId', align: 'center', width: 'auto' },
                { label: '评价计划号', prop: 'planCode', align: 'center', width: 'auto' },
                { label: '评价模型版本', prop: 'version', align: 'center', width: 'auto' },
            ]
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pak: state => state.dict.dt_srm_pak,
            dt_srm_pb3: state => state.dict.dt_srm_pb3,
            dt_srm_pb2: state => state.dict.dt_srm_pb2,

        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_pak', 'dt_srm_pb3', 'dt_srm_pb2'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //评价类别评价阶段联动
        getPhaseData (val) {
            if (val) {
                let dto = {
                    currentPage: 1,
                    pageSize: 15,
                    parentCode: val
                }
                queryRecords(dto).then(res => {
                    let options = [this.dt_srm_p0]
                    res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state'])
                    this.phaseList = res.data.list
                })
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            this.list = [
                { state: '评价发起', evaluationType: '日常评价', evaluationPhase: '三季度', year: '2019', supplierCode: '1008', supplierName: '江西腾勒动力有限公司', evaluateClass: 'D-交付', score: '', rank: '', evaluate: '', evaluationPerson: 'srm', submitDate: '', orgCode: 'PV采购组织', evaluationCode: '', rowId: '', planCode: '', version: '' },
                { state: '评价发起', evaluationType: '日常评价', evaluationPhase: '三季度', year: '2019', supplierCode: '1008', supplierName: '江西腾勒动力有限公司', evaluateClass: 'Q-质量', score: '', rank: '', evaluate: '', evaluationPerson: 'srm', submitDate: '', orgCode: 'PV采购组织', evaluationCode: '', rowId: '', planCode: '', version: '' }
            ]
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //提交按钮单击事件
        handleSubmit () {

        },
        //行内评按钮单击事件
        handleEvaluate (index, val) {
            console.log(index, val)
            this.$refs.addMain.dialogVisible = true
        },
        //行内查询按钮单击事件
        handleSearch (index, val) {
            console.log(index, val)
            this.$refs.addMain.dialogVisible = true
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        }
    }
};
