import * as api from "@/api/project/srm/contractManage/specialPaymentMethod"
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import addMain from './dialog/addMain'//下拉组件
export default {
    name: "supplierFeedback",
    components: { Pagination, caiGou, dialogGysxz, addMain },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            purchaseOrgName: null,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            temp: {},
            dialogVisible: false,
            dialogVisibleFeedback: false,
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pdr: state => state.dict.dt_srm_pdr,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
            dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    filters: {
        statusFilter: function (a) {
            if (a === '1') {
                return "同意"
            } else if (a == '0') {
                return "不同意"
            }
        },
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取采购组织名称
        selectName (val) {
            this.purchaseOrgName = val
        },
        handleSupplier () {
            this.$refs.refGys.dialogVisible = true
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
        },
        //编辑成功之后刷新数据
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu, this.dt_srm_pdr, this.dt_srm_pdt, this.dt_srm_pdu]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['state', 'regularPaymentType', 'regularSettlementType', 'regularPaymentPeriod', 'feedbackPaymentType', 'feedbackSettlementType', 'feedbackPaymentPeriod'])
                this.list = res.data.list.map(v => {
                    v.regularPayment = v.regularPaymentTypeCN + '/' + v.regularSettlementTypeCN + '/' + v.regularPaymentPeriodCN
                    if (v.feedbackPayment) {
                        v.feedbackPayment = v.feedbackPaymentTypeCN + '/' + v.feedbackSettlementTypeCN + '/' + v.feedbackPaymentPeriodCN
                    }
                    return v
                })
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleUpdate (index, val) {
            console.log(val, 'bb')
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...val }
            this.$nextTick(() => {
                if (this.$refs.addMain.temp.feedbackResult === '1') {
                    this.$refs.addMain.showIsItem = false
                    this.$refs.addMain.temp.feedbackPaymentPeriod = val.regularPaymentPeriod
                    this.$refs.addMain.temp.feedbackPaymentType = val.regularPaymentType
                    this.$refs.addMain.temp.feedbackSettlementType = val.regularSettlementType
                } else {
                    this.$refs.addMain.showIsItem = true
                    this.$refs.addMain.temp.feedbackPaymentPeriod = val.feedbackPaymentPeriod
                    this.$refs.addMain.temp.feedbackPaymentType = val.feedbackPaymentType
                    this.$refs.addMain.temp.feedbackSettlementType = val.feedbackSettlementType
                }
            })
        },
        handleEffect (index, val) {
            this.$confirm("是否提交反馈", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                let dto = {
                    method: 'feedback',
                    rowIds: [val.rowId]
                }
                api.updateEffect(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '反馈成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },

        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
