import * as api from '@/api/project/mes/dataBase/stationEquipment/stationEquipmentSelect'
import Pagination from '@/components/Pagination'

export default {
    name: 'stationEquipmentSelect',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false
        }
    },
    mounted() {
        this.getEquipmentData()
    },
    methods: {
        checked(i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.listSelected = []
        },
        // 父级工位表格展示已选，modal已选勾选
        getListSelected() {
            this.listSelected = this.$parent.$parent.$parent.list_b
        },
        //检查是否取消勾选
        checkUnsel(val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.equipmentCode === row.equipmentCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain(val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.equipmentCode === items.equipmentCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.equipmentCode !== row.equipmentCode
                })
            }
        },
        selectAll(val) {
            if (val.length == 0) {
                this.list.forEach(item => {
                    this.listSelected.forEach((item2, index) => {
                        if (item.equipmentCode == item2.equipmentCode) {
                            this.listSelected.splice(index, 1)
                        }
                    })
                })
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.equipmentCode === items.equipmentCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 在备选中把已选打钩
        getEquipmentData() {
            api.getEquipmentAll(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.equipmentCode === items.equipmentCode) {
                                this.$nextTick(function() {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        }
    }
}
