import * as api from '@/api/project/mes/dataBase/testStandard'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'testStandard',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                inspectionCode: undefined,
                inspectionName: undefined,
                inspectionType: undefined
            },
            temp: {
                assessTechnology: undefined,
                controlMethod: undefined,
                detectionStandard: undefined,
                frequency: undefined,
                inspectionCode: undefined,
                inspectionMode: undefined,
                inspectionName: undefined,
                inspectionType: undefined,
                normTolerance: undefined,
                number: undefined,
                processOperation: undefined,
                reactionPlan: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            rules: {
                inspectionName: [
                    {
                        required: true,
                        message: '检验项目名称不能为空',
                        trigger: 'blur'
                    }
                ],
                inspectionType: [
                    {
                        required: true,
                        message: '检验类型不能为空',
                        trigger: 'blur'
                    }
                ],
                inspectionMode: [
                    {
                        required: true,
                        message: '检验方式不能为空',
                        trigger: 'blur'
                    }
                ],
                detectionStandard: [
                    {
                        required: true,
                        message: '检验标准不能为空',
                        trigger: 'blur'
                    }
                ]
            }
        }
    },
    filters: {
        inspectionTypeFilter: function(e) {
            if (e === 'inspection_type_sc') {
                return '抽检'
            } else {
                return '巡检'
            }
        }
    },
    computed: {
        ...mapState({
            dt_inspection_type: state => state.mesDict.dt_inspection_type,
            dt_inspection_method: state => state.mesDict.dt_inspection_method
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', [
            'dt_inspection_type',
            'dt_inspection_method'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = this.$myFun.codeToName(
                    res.data,
                    [this.dt_inspection_method],
                    ['inspectionMode']
                ).list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery() {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate() {
            //新增
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create() {
            //新增确认按钮
            this.$refs.temp.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                } else {
                    return false
                }
            })
        },
        cancel() {
            //取消弹窗按钮
            this.dialogFormVisible = false
        },
        // 获取表格选中时的数据
        selectRow(val) {
            this.selectlistRow = val
        },

        handleUpdate() {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.dialogFormVisible = true
                this.temp = this.selectlistRow[0] // copy obj
                api.getRecord(this.temp.inspectionCode).then(res => {
                    this.temp = res.data
                    this.dialogStatus = 'update'
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update() {
            //编辑提交
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.updateRecord(this.temp.inspectionCode, this.temp).then(
                        () => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        }
                    )
                }
            })
        },
        handleDelete() {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        api.deleteRecord(
                            this.selectlistRow[0].inspectionCode
                        ).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },

        resetTemp() {
            this.temp = {
                assessTechnology: undefined,
                controlMethod: undefined,
                detectionStandard: undefined,
                frequency: undefined,
                inspectionCode: undefined,
                inspectionMode: undefined,
                inspectionName: undefined,
                inspectionType: undefined,
                normTolerance: undefined,
                number: undefined,
                processOperation: undefined,
                reactionPlan: undefined
            }
        },
        handleClose() {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        //点击表格某一行
        rowClick(val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
