import { queryDeviceAlarm } from "@/api/project/iot/PM/equipmentProducts"
import Pagination from "@/components/Pagination"
import throttle from 'lodash/throttle'
export default {
    name: "DAInfo",
    components: { Pagination },
    props: ['projectList', 'deviceList', 'code'],
    watch: {
        'listQuery.projectName' (val) {
            if (!val) {
                this.listQuery.projectCode = undefined
            }
        },
        'listQuery.deviceName' (val) {
            if (!val) {
                this.listQuery.deviceCode = undefined
            }
        },
    },
    data () {
        return {
            theight: 0,//表格高度
            listLoading: false,
            list: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                deviceCode: this.code,
                projectName: undefined,
                projectCode: undefined,
                // deviceCode: undefined,
                // deviceName: undefined,
            },
            tableKey: 0 //表格索引
        }
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            queryDeviceAlarm(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        toInfo (code) {
            this.$emit('setCode', code)
            this.$emit('setPage', 'DA-Info')
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
        querySearchProject (queryString, cb) {
            var restaurants = this.projectList
            var results = queryString ? restaurants.filter(this.createFilterProject(queryString)) : restaurants
            cb(results)
        },
        createFilterProject (queryString) {
            return (restaurant) => {
                return (restaurant.projectName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectProject (item) {
            this.listQuery.projectCode = item ? item.projectCode : undefined
            this.listQuery.projectName = item ? item.projectName : undefined
        },
        querySearchDevice (queryString, cb) {
            var restaurants = this.deviceList
            var results = queryString ? restaurants.filter(this.createFilterDevice(queryString)) : restaurants
            cb(results)
        },
        createFilterDevice (queryString) {
            return (restaurant) => {
                return (restaurant.typeName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1)
            }
        },
        handleSelectDevice (item) {
            this.listQuery.deviceCode = item ? item.typeCode : undefined
            this.listQuery.deviceName = item ? item.typeName : undefined
        }
    }
};
