$minColor: skyblue;
$minPadding: 15px;
$BBC: 1px solid #ededed;
.line-box {
    background-color: #fff;
    .lin-title {
        position: relative;
        padding: $minPadding;
        border-bottom: $BBC;
        font-size: 12px;
        &::before {
            content: '';
            position: absolute;
            left: 4px;
            top: 50%;
            width: 4px;
            height: 80%;
            background-color: $minColor;
            transform: translateY(-50%);
        }
    }
    .line-contern {
        padding: $minPadding;
        .content-box {
            display: flex;
            justify-content: space-between;
            flex-wrap: wrap;
            border-bottom: $BBC;
            padding: 0 10px;
            padding-bottom: 0;
            min-height: 50px;
            .holder {
                opacity: 0;
            }
            .content-box-item {
                display: flex;
                justify-content: space-between;
                flex-direction: column;
                width: 18%;
                background-color: #252525;
                border-radius: 5px;
                margin-bottom: $minPadding;
                padding: 10px;
                color: #fff;
                p {
                    font-size: 12px;
                    text-align: center;
                    &:nth-of-type(1) {
                        font-size: 14px;
                        font-weight: 600;
                    }
                    &:nth-of-type(2) {
                        overflow: hidden;
                        text-overflow: ellipsis;
                        white-space: nowrap;
                        text-align: center;
                    }
                }
                .eng-body-box {
                    display: flex;
                    width: 100%;
                    flex-direction: column;
                    justify-content: space-between;
                    .eng-body {
                        display: flex;
                        justify-content: space-between;
                        color: $minColor;
                        &:nth-of-type(2) {
                            margin-top: 10px;
                        }
                        span {
                            text-align: center;
                            padding: 5px 0;
                        }
                        span:nth-of-type(2n-1) {
                            flex: 1;
                            background-color: $minColor;
                            color: #fff;
                        }
                        span:nth-of-type(2n) {
                            flex: 2;
                            background-color: #fff;
                        }
                    }
                }
            }
        }
        .eng-footer {
            p {
                text-align: center;
                cursor: pointer;
            }
        }
    }
}
