import { mapState } from "vuex"
export default {
    name: 'dialogSet',
    components: {},
    props: ['title'],
    data () {
        return {
            isEdit: false,
            iscgEdit: false,//采购是否可编辑
            formData: {
                qualification: false,
                introduction: false,
                qualifiedType: null,
                introduceType: null,
                questionType: null,
                feedbackDate: null,
                completionDate: null,
                questionDesc: null
            },
            dialogVisible: false,
            formConfig: [
                { type: 'checkbox', label: '资质合格', prop: 'qualification', width: '' },
                { type: 'checkbox', label: '是否引入', prop: 'introduction', width: '' },
                { type: 'dictSelect', dType: 'dt_srm_pae', label: '资质类型', prop: 'qualifiedType', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pao', label: '引入类型', prop: 'introduceType', width: '', class: 'filter-item ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pap', label: '问题类型', prop: 'questionType', width: '', class: 'filter-item ml_5' },
                { type: 'date', label: '要求反馈日期', prop: 'feedbackDate', width: '' },
                { type: 'date', label: '要求整改完成日期', prop: 'completionDate', width: '' },
                { type: 'textarea', label: '问题描述', prop: 'questionDesc', width: '800px', rows: "3" }
            ]
        }
    },
    created () {

    },
    computed: {
        ...mapState({
            dt_srm_pae: state => state.dict.dt_srm_pae,//资质类型
            dt_srm_pao: state => state.dict.dt_srm_pao,//引入类型
            dt_srm_pap: state => state.dict.dt_srm_pap//问题类型
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pae", "dt_srm_pao", "dt_srm_pap"])
    },
    methods: {
        handleConfirm () {
            this.$refs.refForm.validate((valid) => {
                if (valid) {
                    this.$confirm('确认保存吗?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }).then(() => {
                        this.$emit('setData', this.formData)
                        this.dialogVisible = false
                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消'
                        })
                    })
                }
            })
        },
        initForm () {
            this.$refs['refForm'].resetFields()
            this.$refs['refForm'].clearValidate()
        }
    }
}
