import selectTree from './selectTree'
import * as api from '@/api/project/eas/database/eqmt'
export default {
    name: 'eqmtSelectTree',
    components: {
        selectTree
    },
    data() {
        return {
            // 数据默认字段
            defaultProps: {
                rowId: 'rowId', // 父级唯一标识
                children: 'children',
                label: 'typeName'
            },
            // 数据列表
            options: [],
            default: undefined
        }
    },
    mounted() {
        //获取数据
        api.queryRecords(this.listQuery).then(res => {
            this.options = this.$myFun.buildTree(
                res.data,
                'typeCode',
                'superiorCode'
            )[0].children
        })
    },
    methods: {
        getValue(val) {
            this.$emit('change', val)
        }
    }
}
