import * as api from "@/api/project/srm/supplierManagement/supplierRegister"

export default {
    data () {
        return {
            dialogStatus: '',
            dialogVisible: false,
            temp: {},
        }
    },
    methods: {
        create () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecordgylc(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('getFreshList')
                    })
                } else {
                    return false
                }
            })

        },
        update () {
            api.updateRecordgylc(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        },
        resetTemp () {
            this.temp = {
                procedureNumber: undefined,
                technologicalProcess: undefined,
                productFeatures: undefined,
                processFeatures: undefined,
                remark: undefined,
                supplierId: this.temp.supplierId
            }
        }
    }
}
