import { selectlists as listOrg } from "@/api/system/organization";
export default {
    name: "org-select",
    props: {
        orgType: {
            type: String
        },
        businessFunction: {
            type: String
        },
        organizationCode: {
            required: true,
            default: ""
        },
        disabled: {
            type: Boolean,
            default: false
        },
        placeholder: {
            type: String,
            default: "请选择"
        }
    },
    model: {
        prop: "organizationCode",
        event: "change"
    },
    data () {
        return {
            options: [],
            itemKey: this.organizationCode
        };
    },
    watch: {
        organizationCode () {
            this.itemKey = this.organizationCode;
        }
    },
    created () {
        this.getOptions()
    },
    methods: {
        getOptions () {
            const listQuery = {
                type: this.orgType,
                businessFunction: this.businessFunction,
                state: 1
            };
            listOrg(listQuery).then(res => {
                this.options = res.data;
                this.$emit("org", this.options);
            });
        },
        changeSelect (val) {
            this.$emit("change", val);
            let name = "";
            let org = this.options
                .filter(v => v.organizationCode == val)
                .find((value) => value);
            if (org) {
                name = org.organizationName;
            }

            this.$emit("selectName", name);
        }
    }
};
