import * as api from '@/api/project/ems/energyPlanAdministration/factoryEnergyPlan.js'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    name: 'factoryEnergyPlan',
    components: { Pagination },
    data () {
        return {
            list: null,
            unitsList: null,
            unitsListSub: null,
            linesList: null,
            linesListSub: null,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                factoryCode: undefined // 工厂
            },
            temp: {
                factoryCode: undefined,
                waterEnergy: undefined,
                electricEnergy: undefined,
                gasEnergy: undefined,
                nextMonthDate: undefined,
                remark: ''
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            total: 0,
            theight: 0, //表格高度
            selectlistRow: null,
            rules: {
                // factoryCode: [
                //     {
                //         required: true,
                //         message: '请选择工厂',
                //         trigger: 'blur'
                //     }
                // ],
                waterEnergy: [
                    {
                        required: true,
                        message: '请输入水消耗',
                        trigger: 'blur'
                    }
                ],
                electricEnergy: [
                    {
                        required: true,
                        message: '请输入电消耗',
                        trigger: 'blur'
                    }
                ],
                gasEnergy: [
                    {
                        required: true,
                        message: '请输入气电消耗',
                        trigger: 'blur'
                    }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_gc_factory_list: state =>
                state.businessComponent.dt_gc_factory_list,
            dt_quarter: state => state.emsDict.dt_quarter
        })
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_gc_factory_list'
        ])
        this.getList()
    },
    destroyed () {
        this.setTableHeight()
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_gc_factory_list'
        ])
        this.getList()
    },
    methods: {
        getList () {
            api.getDTOs(this.listQuery).then(res => {
                res.data.list.map((v, i) => {
                    v.serial = i + 1
                })
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleUpdate () {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.temp = this.selectlistRow[0] // copy obj
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                //编辑
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        api.patchDTO(this.temp.rowId, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let rowIds = []
                        this.selectlistRow.forEach((val, index) => {
                            rowIds[index] = val.rowId
                        })
                        api.deleteDTO(rowIds).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.currentPage = 1
            this.listQuery.factoryCode = undefined
            this.getList()
        },
        resetTemp () {
            this.temp = {}
        },
        handleClose () {
            this.resetTemp()
        },
        setTableHeight () {
            //表格高度计算
            this.theight = this.$myFun.getSingleTbHeight()
        },
        selectRow (val) {
            // 获取表格选中时的数据
            this.selectlistRow = val
        }
    }
}
