import * as api from '@/api/project/ems/indexManagement/workshopEnergy.js'
import Pagination from '@/components/Pagination'
import caigou from '@/components/Select/caigou'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    name: 'workshopEnergy',
    components: { Pagination, caigou },
    data () {
        return {
            list: null,
            unitsList: null,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                factoryCode: undefined, // 工厂
                workShopCode: undefined // 企业
            },
            temp: {
                waterConsume: undefined,
                eleConsume: undefined,
                gasConsume: undefined,
                factoryCode: undefined,
                workShopCode: undefined,
                quarter: undefined,
                remark: ''
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            total: 0,
            theight: 0, //表格高度
            selectlistRow: null,
            rules: {
                factoryCode: [
                    {
                        required: true,
                        message: '请选择工厂',
                        trigger: 'blur'
                    }
                ],
                workShopCode: [
                    {
                        required: true,
                        message: '请选择车间',
                        trigger: ['blur', 'change']
                    }
                ],
                waterConsume: [
                    {
                        required: true,
                        message: '请输入水消耗',
                        trigger: 'blur'
                    }
                ],
                eleConsume: [
                    {
                        required: true,
                        message: '请输入电消耗',
                        trigger: 'blur'
                    }
                ],
                gasConsume: [
                    {
                        required: true,
                        message: '请输入气电消耗',
                        trigger: 'blur'
                    }
                ],
                quarter: [
                    {
                        required: true,
                        message: '请选择季度',
                        trigger: 'blur'
                    }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    watch: {
        'temp.factoryCode' (value) {
            if (value) {
                api.queryUnits(value).then(res => {
                    this.unitsList = res.data
                    let ret = res.data.find((v) => {
                        return v.workCenterCode == this.temp.workShopCode
                    })
                    if (res.data.length) {
                        if (!ret) {
                            // this.temp.workShopCode = res.data[0].workCenterCode
                            this.$set(this.temp, 'workShopCode', res.data[0].workCenterCode)
                        }
                    } else {
                        // this.temp.workShopCode = undefined
                        this.$set(this.temp, 'workShopCode', undefined)
                    }
                })
            } else {
                // this.temp.workShopCode = undefined
                this.$set(this.temp, 'workShopCode', undefined)
            }
        }
    },
    computed: {
        ...mapState({
            dt_quarter: state => state.emsDict.dt_quarter
        })
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.getList()
    },
    destroyed () {
        this.setTableHeight()
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.getList()
    },
    methods: {
        getName (name) {
            this.temp.factoryName = name
        },
        bug () {
            this.$forceUpdate()
        },
        getList () {
            api.getDTOs(this.listQuery).then(res => {
                res.data.list.map((v, i) => {
                    v.serial = i + 1
                })
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        selectChange () {
            api.queryUnits(this.listQuery.factoryCode).then(res => {
                this.listQuery.workShopCode = undefined
                this.unitsList = res.data
            })
        },
        selectDialogChange () {
            api.queryUnits(this.temp.factoryCode).then(res => {
                this.temp.workShopCode = undefined
                this.unitsList = res.data
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true

        },
        create: throttle(
            function () {
                this.$refs.addref.validate(valid => {
                    if (valid) {
                        api.postDTO(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$refs.addref.clearValidate()
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate () {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.temp = JSON.parse(JSON.stringify(this.selectlistRow[0])) // copy obj
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                //编辑
                this.$refs['addref'].validate(valid => {
                    if (valid) {
                        api.patchDTO(this.temp.rowId, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$refs.addref.clearValidate()
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let rowIds = []
                        this.selectlistRow.forEach((val, index) => {
                            rowIds[index] = val.rowId
                        })
                        api.deleteDTO(rowIds).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.currentPage = 1
            this.listQuery.factoryCode = undefined
            this.listQuery.workShopCode = undefined
            this.getList()
        },
        resetTemp () {
            this.temp = {}
        },
        handleClose () {
            this.resetTemp()
            this.$refs.addref.clearValidate()
            this.$refs.addref.resetFields()
        },
        setTableHeight () {
            //表格高度计算
            this.theight = this.$myFun.getSingleTbHeight()
        },
        selectRow (val) {
            // 获取表格选中时的数据
            this.selectlistRow = val
        }
    }
}
