import Pagination from '@/components/Pagination' // Secondary package based on el-pagination
import * as api from '@/api/project/mes/dataBase/stationProduct/stationProduct'
import stationProductSelect from '@/components/Select/stationProductSelect'
import stationProductSelect2 from '@/components/Select/stationProductSelect2'
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'stationEquipment',
    components: { Pagination, stationProductSelect, stationProductSelect2 },
    data() {
        return {
            theight: 0, //表格高度
            workCenterData: [],
            select_a: [],
            select_b: [],
            select_c: [],
            list_a: [],
            list_b: [],
            list_c: [],
            total_a: 0,
            total_b: 0,
            total_c: 0,
            listQuery_a: {
                currentPage: 1,
                pageSize: 10,
                stationCode: undefined,
                stationName: undefined
            },
            listQuery_b: {
                currentPage: 1,
                pageSize: 50,
                equipmentCode: undefined,
                equipmentName: undefined
            },
            listQuery_c: {
                currentPage: 1,
                pageSize: 10,
                equipmentCode: undefined,
                equipmentName: undefined
            },
            dialogFormVisible: false,
            dialogFormVisible2: false,
            dialogStatus: '',
            dialogStatus2: '',
            temp: [],
            temp2: []
        }
    },
    filters: {
        isEnableFilter: function(e) {
            if (e === '1') {
                return '是'
            } else {
                return '否'
            }
        }
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_inspection_type: state => state.mesDict.dt_inspection_type,
            dt_inspection_method: state => state.mesDict.dt_inspection_method
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', [
            'dt_inspection_type',
            'dt_inspection_method'
        ])
        this.setTableHeight()
        this.getWorkCenterData()
        this.getList_a()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight() + 20
        },
        getWorkCenterData() {
            api.getWorkCenterList().then(res => {
                this.workCenterData = res.data
            })
        },
        //单击行绑定点击事件
        rowClick_a(val) {
            this.$refs.tb_a.toggleRowSelection(val, 'selected')
        },
        rowClick_b(val) {
            this.$refs.tb_b.toggleRowSelection(val, 'selected')
        },
        rowClick_c(val) {
            this.$refs.tb_c.toggleRowSelection(val, 'selected')
        },
        // 查询工位列表
        getList_a() {
            this.list_b = []
            this.list_c = []
            api.getStationList(this.listQuery_a).then(res => {
                this.list_a = res.data.list
                this.total_a = res.data.pages.count
            })
        },
        // 查询工位对应产品
        getList_b() {
            api.getStationEquipmentList(
                this.listQuery_b,
                this.select_a[0].stationCode
            ).then(res => {
                this.list_b = res.data.list
                this.total_b = res.data.pages.count
                // if (this.$refs.stationEquipmentSelect) {
                //     this.$refs.stationEquipmentSelect.listSelected = this.list_b
                // }
            })
        },
        // 查询产品对应检验项
        getList_c() {
            api.getProductCheckList(
                this.listQuery_b,
                this.select_a[0].stationCode,
                this.select_b[0].materielCode
            ).then(res => {
                this.list_c = this.$myFun.codeToName(
                    res.data,
                    [this.dt_inspection_method, this.dt_inspection_type],
                    ['inspectionMode', 'inspectionType']
                ).list
                this.total_c = res.data.pages.count
            })
        },
        selectRow_a(val) {
            this.select_a = val
            if (val.length == 0) {
                this.select_a = []
                this.list_b = []
                return false
            }
            if (val.length > 1) {
                this.$refs.tb_a.clearSelection()
                this.$refs.tb_a.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb_a.setCurrentRow(this.select_a[0])
            if (this.select_a.length) {
                this.getList_b()
            }
        },
        selectRow_b(val) {
            this.select_b = val
            if (val.length == 0) {
                this.select_b = []
                this.list_c = []
                return false
            }
            if (val.length > 1) {
                this.$refs.tb_b.clearSelection()
                this.$refs.tb_b.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb_b.setCurrentRow(this.select_b[0])
            if (this.select_b.length) {
                this.getList_c()
            }
        },
        selectRow_c(val) {
            this.select_c = val
            if (val.length > 1) {
                this.$refs.tb_c.clearSelection()
                this.$refs.tb_c.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb_c.setCurrentRow(this.select_c[0])
        },
        handleCreate() {
            if (Array.isArray(this.select_a) && this.select_a[0]) {
                //新增按钮单击事件方法
                this.dialogStatus = 'create'
                this.dialogFormVisible = true
                setTimeout(() => {
                    this.$refs.stationEquipmentSelect.getListSelected()
                    this.$refs.stationEquipmentSelect.getEquipmentData()
                }, 0)
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择工位',
                    type: 'warning'
                })
            }
        },
        handleCreate2() {
            if (Array.isArray(this.select_b) && this.select_b[0]) {
                //新增按钮单击事件方法
                this.dialogStatus2 = 'create'
                this.dialogFormVisible2 = true
                setTimeout(() => {
                    this.$refs.stationEquipmentSelect2.getListSelected()
                    this.$refs.stationEquipmentSelect2.getEquipmentData()
                }, 0)
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品',
                    type: 'warning'
                })
            }
        },
        handleDelete() {
            api.DeleteStationEquipmentList(
                this.select_b[0],
                this.select_a[0].stationCode
            ).then(res => {
                if (res.status == 204) {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_b()
                }
            })
        },
        handleDelete2() {
            api.DeleteProductCheck(
                this.select_c[0],
                this.select_a[0].stationCode,
                this.select_b[0].productCode
            ).then(res => {
                if (res.status == 204) {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_c()
                }
            })
        },
        resetTemp() {
            this.temp = []
        },
        resetTemp2() {
            this.temp2 = []
        },
        handleClose() {
            this.$refs.stationEquipmentSelect.$refs.tb_bx.clearSelection()
            this.$refs.stationEquipmentSelect.listSelected = []
            this.getList_b()
        },
        handleClose2() {
            this.$refs.stationEquipmentSelect2.$refs.tb_bx.clearSelection()
            this.$refs.stationEquipmentSelect2.listSelected = []
            this.getList_c()
        },
        // 更新工位产品
        update() {
            this.list_b.forEach(item => {
                item.productCode = item.materielCode
                delete item.materielCode
            })
            api.patchStationEquipmentList(
                this.list_b,
                this.select_a[0].stationCode
            ).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '更新成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_b()
                    this.dialogFormVisible = false
                }
            })
        },
        // 添加检验项
        update2() {
            api.patchProductCheck(
                this.list_c,
                this.select_a[0].stationCode,
                this.select_b[0].productCode
            ).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '更新成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList_c()
                    this.dialogFormVisible2 = false
                }
            })
        }
    }
}
