import * as api from '@/api/project/ems/dataBase/energyFlatWind'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    name: 'energyFlatWind',
    components: { Pagination },
    data () {
        return {
            list: null,
            purpose: null,
            total: 0,
            theight: 0, //表格高度
            selectlistRow: null, //选取表格数据
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                energyTypeCode: undefined,
                purpose: undefined,
                periodType: undefined
            },
            temp: {
                quarter: undefined,
                dateRange: undefined,
                // dateRangeStart: undefined,
                dateStartRange: undefined,
                // dateRangeEnd: undefined,
                dateEndRange: undefined,
                energyTypeCode: undefined,
                periodType: undefined,
                purpose: undefined,
                price: undefined,
                unit: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            rules: {
                energyTypeCode: [
                    {
                        required: true,
                        message: '请选择能耗分类编码',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                periodType: [
                    {
                        required: true,
                        message: '请选择时段类型',
                        trigger: 'blur'
                    }
                ],
                purpose: [
                    { required: true, message: '请选择用途', trigger: 'blur' },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                dateRange: [
                    {
                        required: true,
                        message: '请选择时间范围',
                        trigger: 'blur'
                    }
                ],
                price: [
                    { required: true, message: '请输入价格', trigger: 'blur' },
                    {
                        required: true,
                        message: '请填写正确的金额',
                        pattern: /(^[\d]|^[1-9][\d]*)($|[.][\d]*$)/
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                unitName: [
                    { required: true, message: '请选择单位', trigger: 'blur' },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                quarter: [
                    {
                        required: true,
                        message: '请选择有效期',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    mounted () {
        this.$store.dispatch('emsDict/getDicData', [
            'dt_purpose',
            'dt_ladder_unit',
            'dt_period_type',
            'dt_quarter'
        ])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_nh_energyType_list'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    destroyed () {
        this.getList()
        this.$store.dispatch('emsDict/getDicData', [
            'dt_purpose',
            'dt_ladder_unit',
            'dt_period_type',
            'dt_quarter'
        ])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_nh_energyType_list'
        ])
    },
    computed: {
        ...mapState({
            dt_purpose: state => state.emsDict.dt_purpose,
            dt_ladder_unit: state => state.emsDict.dt_ladder_unit,
            dt_period_type: state => state.emsDict.dt_period_type,
            dt_quarter: state => state.emsDict.dt_quarter,
            dt_nh_energyType_list: state =>
                state.businessComponent.dt_nh_energyType_list
        })
    },
    methods: {
        getList () {
            api.queryFlatWind(this.listQuery).then(res => {
                if (res.data) {
                    res.data.list.map((v, i) => {
                        v.serial = i + 1
                    })
                    this.list = res.data.list
                    this.total = res.data.pages.count
                }
            })
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        //编辑弹窗
        handleUpdate () {
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                api.queryFlatWindItem(this.selectlistRow[0].rowId).then(res => {
                    this.temp = res.data
                    this.temp.dateRange = [
                        res.data.dateStartRange,
                        res.data.dateEndRange
                    ]
                    this.dialogStatus = 'update'
                    this.dialogFormVisible = true
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //编辑
        update: throttle(
            function () {
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        this.temp.dateStartRange = this.temp.dateRange[0]
                        this.temp.dateEndRange = this.temp.dateRange[1]
                        this.temp.dateRange = undefined
                        api.updateFlatWind(this.temp.rowId, this.temp).then(
                            () => {
                                this.getList()
                                this.dialogFormVisible = false
                                this.$message({
                                    title: '成功',
                                    message: '更新成功',
                                    type: 'success',
                                    duration: 2000
                                })
                            }
                        )
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除列表
        handleDelete () {
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.rowId
                        })
                        api.deleteFlatWind(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        this.temp.dateStartRange = this.temp.dateRange[0]
                        this.temp.dateEndRange = this.temp.dateRange[1]
                        this.temp.dateRange = undefined
                        api.createFlatWind(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {}
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        // 搜索
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 清除查询条件
        deleteQuery () {
            this.listQuery.currentPage = 1
            this.listQuery.energyTypeCode = undefined
            this.listQuery.purpose = undefined
            this.listQuery.periodType = undefined
            this.getList()
        }
    }
}
