import * as api from '@/api/project/mes/dataBase/bom'  // 增、删、改api引入
import orgSelect from '@/components/Select/orgSelect.vue'
import { DT_ORG_TYPE } from '@/utils/constant'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'  // 基本数据引入
import workStationTable from "./workStationTable";


export default {
    name: 'bom',
    components: { Pagination,orgSelect,workStationTable },
    data () {
        return {
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            materielData: [],
            list: [],
            fromList: [],
            total: 0,
            tableKey: [0, 1],//表格索引
            listQuery: {
                dt_bom_type: undefined,
                dt_bom_category: undefined,
                dt_bom_rank: undefined,
                dt_org_code: undefined,
                materielCode: undefined,
                //查询
                currentPage: 1,
                pageSize: 15
            },
            listQuery1: {
                //从表查询
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                // bomId: undefined,
                materielCode: undefined,
                productName: undefined,
                cardinalNumber: undefined,
                version: undefined,
                // status: undefined,
                productionNumber: undefined,
                orderNumber: undefined,
                organizationCode: undefined,
                bomType: undefined,
                bomCategory: undefined,
                bomRank: undefined,
                startDate: undefined,
                endDate: undefined,
                timeRange: undefined
            },
            tempFrom: {
                assembleLine: undefined,
                assembleQuantity: undefined,
                bomId: undefined,
                endDate: undefined,
                manufactureLine: undefined,
                materielCode: undefined,
                startDate: undefined,
                workStationCode: undefined,
                timeRange: undefined
            },
            DT_ORG_TYPE: DT_ORG_TYPE,
            dialogStatus: '',
            dialogFormVisible: false,
            fromDialogStatus: '',
            fromDialogFormVisible: false,
            workStationDialogVisible:false,
            currentSelectedRow: null, //当前选中的行
            selectedRows: null, //多选时选中的所有行
            selectFromList: null, //从表选中
            rules: {
                bomType: [
                    { required: true, message: '请填写BOM类型', trigger: "blur" }
                ],
                bomRank: [
                    { required: true, message: '请填写BOM类别', trigger: "blur" }
                ],
                bomCategory: [
                    { required: true, message: '请填写BOM级别', trigger: "blur" }
                ],
                organizationCode: [
                    { required: true, message: '请填写组织编码', trigger: "blur" }
                ],
                materielCode: [
                    { required: true, message: '请填写产品编码', trigger: "blur" }
                ]
            },
            formRules: {
                materielCode: [
                    { required: true, message: '请填写产品编码', trigger: "blur" }
                ]
            }
        }
    },
    created () {
        this.getList()
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_bom_type: state => state.mesDict.dt_bom_type,
            dt_bom_category: state => state.mesDict.dt_bom_category,
            dt_bom_rank: state => state.mesDict.dt_bom_rank,
            dt_material_property: state => state.mesDict.dt_material_property,
            dt_assemble_line: state => state.mesDict.dt_assemble_line,
            dt_manufacture_line: state => state.mesDict.dt_manufacture_line
        })
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        'temp.timeRange' (value) {
            if (value) {
                this.temp.startDate = value[0];
                this.temp.endDate = value[1];
            }
        },
        'tempFrom.timeRange' (value) {
            if (value) {
                this.tempFrom.startDate = value[0];
                this.tempFrom.endDate = value[1];
            }
        }
    },
    mounted () {
        this.$store.dispatch('mesDict/getDicData', [
            'dt_bom_type',
            'dt_bom_category',
            'dt_bom_rank',
            'dt_material_property',
            'dt_assemble_line',
            'dt_manufacture_line'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        getList () {
            //获取表格数据
            api.getMain_a(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        resetTemp () {
            this.temp = {
                // bomId: undefined,
                materielCode: undefined,
                productName: undefined,
                cardinalNumber: undefined,
                version: undefined,
                // status: undefined,
                productionNumber: undefined,
                orderNumber: undefined,
                organizationCode: undefined,
                bomType: undefined,
                bomCategory: undefined,
                bomRank: undefined,
                startDate: undefined,
                endDate: undefined,
                timeRange: undefined
            }
        },
        handleQuery () {
            //表格查询
            this.listQuery.currentPage = 1
            this.getList()
            this.fromList = undefined;
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create () {
            //编辑确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    if (new Date(this.temp.startDate).getTime() > new Date(this.temp.endDate).getTime()) {
                        this.$message({
                            title: '警告',
                            message: '日期区间错误',
                            type: 'warning'
                        })
                        return false;
                    }
                    // 添加bom
                    api.creatMain_a(this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                }
            })
        },
        handleUpdate () {
            //编辑按钮单击事件方法
            if (this.selectedRows) {
                this.temp = this.selectedRows
                api.getMainBy_a(this.selectedRows.rowId).then(res => {
                    this.temp = res.data;
                    this.temp.timeRange = [res.data.startDate, res.data.endDate];
                    this.dialogStatus = "update";
                    this.dialogFormVisible = true;
                });
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update () {
            //编辑确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.updataMainBy_a(this.selectedRows.rowId, this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '更新成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                }
            })
        },
        updateFrom () {
            api.updataSlave_a(this.selectFromList[0].rowId, this.tempFrom).then(() => {
                this.getFromList()
                this.fromDialogFormVisible = false
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
            })

        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectedRows) {
                this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    api.deleteMain_a([this.selectedRows.rowId]).then(
                        () => {
                            // 清空 bom item
                            this.fromList = [];
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        }
                    )
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消删除'
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handlePublish () {
            //发布
            if (this.selectedRows) {
                api.publishMainBy_a(this.selectedRows.rowId).then(() => {
                    this.getList(),
                        this.$message({
                            title: '成功',
                            message: '发布成功',
                            type: 'success',
                            duration: 2000
                        })
                });
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handleInvalid () {
            //废弃
            if (this.selectedRows) {
                api.invalidMainBy_a(this.selectedRows.rowId).then(() => {
                    this.getList(),
                        this.$message({
                            title: '成功',
                            message: '已废弃',
                            type: 'success',
                            duration: 2000
                        })
                });
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //打开工位弹窗
        handleWorkStationCode(){
            this.workStationDialogVisible=true
        },
        //工位父子传值
        handleDblclickWorkStation(row){
            this.workStationClass=row
        },
        //获取工位
        workStationUpdate(){
            if(this.workStationClass){
                this.tempFrom.workStationCode = this.workStationClass.stationCode;
                this.workStationDialogVisible=false
            }else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }

        },
        selectChange (val) {
            if (!val.length) {
                this.fromList = [];
                return false;
            }
            this.selectedRows = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.tb.clearSelection();
                this.$refs.tb.toggleRowSelection(val[val.length - 1]);
            }
            this.$refs.tb.setCurrentRow(this.selectedRows);
            if (this.selectedRows) {
                this.getFromList();
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },

        //获取从表数据
        getFromList () {
            api.getSlave_a(this.selectedRows.rowId).then(res => {
                this.fromList = res.data
                // this.total1 = res.data.pages.count
            })
        },
        resetFromTemp () {
            this.tempFrom = {
                assembleLine: undefined,
                assembleQuantity: undefined,
                bomId: undefined,
                endDate: undefined,
                manufactureLine: undefined,
                materielCode: undefined,
                startDate: undefined,
                workStationCode: undefined,
                timeRange: undefined
            }
        },
        //从表选择框选中
        selectFromChange (val) {
            this.selectFromList = val
            if (val.length > 1) {
                this.$refs.tb2.clearSelection();
                this.$refs.tb2.toggleRowSelection(val[val.length - 1]);
            }
            this.$refs.tb2.setCurrentRow(this.selectFromList[0]);
        },
        handleCloseFrom () {
            this.resetFromTemp()
            this.$refs.tempFrom.resetFields()
        },
        createFrom () {
            //编辑确认
            this.$refs['tempFrom'].validate(valid => {
                if (valid) {
                    if (new Date(this.tempFrom.startDate).getTime() > new Date(this.tempFrom.endDate).getTime()) {
                        this.$message({
                            title: '警告',
                            message: '日期区间错误',
                            type: 'warning'
                        })
                        return false;
                    }
                    // 添加 bom item
                    this.tempFrom.bomId = this.selectedRows.rowId;
                    api.creatSlave_a(this.tempFrom).then(() => {
                        this.getFromList()
                        this.fromDialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                }
            })
        },
        handleCreateFrom () {
            this.resetFromTemp()
            if (this.selectedRows) {
                this.fromDialogStatus = 'create'
                this.fromDialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品',
                    type: 'warning'
                })
            }
        },
        handleUpdateFrom () {
            if (this.selectedRows) {
                if (Array.isArray(this.selectFromList) && this.selectFromList[0]) {
                    api.getSlaveBy_a(this.selectFromList[0].rowId).then(res => {
                        this.tempFrom = res.data;
                        this.tempFrom.timeRange = [res.data.startDate, res.data.endDate];
                        this.fromDialogStatus = "update";
                        this.fromDialogFormVisible = true;
                    });
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择物料',
                        type: 'warning'
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品',
                    type: 'warning'
                })
            }
        },
        handleDeleteFrom () {
            if (this.selectedRows) {
                if (Array.isArray(this.selectFromList) && this.selectFromList[0]) {
                    this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }).then(() => {
                        api.deleteSlave_a(this.selectFromList[0].rowId).then(
                            () => {
                                this.getFromList()
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                            }
                        )
                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择物料',
                        type: 'warning'
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择产品',
                    type: 'warning'
                })
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        rowClickFrom (val) {
            this.$refs.tb2.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
