import * as api from '@/api/project/eas/el/equipmentWarning'
import Pagination from '@/components/Pagination'

export default {
    name: 'equipmentWarning',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            tableKey: 0,
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecord(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() + 50
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
    }
}
