import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import * as api from "@/api/project/srm/priceManage/actuarialDetailCost"
import Pagination from "@/components/Pagination"
import { DICT_CODE, TWO_STATE_OPTIONS } from "@/utils/constant"

// import { mapState } from 'vuex'


export default {
    name: "quoteInfo",
    components: { Pagination, caiGou },
    props: ['unMutiple', 'title'],
    data () {
        return {
            list: null,
            listSelected: [],
            dialogVisible: false,
            total: 0,
            parentsRows: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                isValid: '1',
                orgCode: undefined,
                quotationMethod: 'PDI2',
                isFixed: '0'
            },
            selectCode: [],
            tableKey: 0,
            tabName: 'first',
            DICT_CODE: DICT_CODE,
            TWO_STATE_OPTIONS: TWO_STATE_OPTIONS,
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '物料编码', prop: 'materielCode', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '物料名称', prop: 'materielName', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商编码', prop: 'supplierCode', width: '', class: 'filter-item ml_5' },
                { type: 'commonInput', label: '供应商名称', prop: 'supplierName', width: '', class: 'filter-item ml_5' },
                { type: 'commonSelect', label: '是否定价', prop: 'isQuote', width: '', optionArr: [{ key: 'state0', value: '已定价' }, { key: 'state1', value: '未定价' }], class: 'filter-item ml_5' }
            ],
            tableConfig: [
                { label: '序号', prop: 'rowId', align: 'center', width: 'auto' },
                { label: '报价时间', prop: 'quotedDate', align: 'center', width: 'auto' },
                { label: '物料编码', prop: 'materielCode', align: 'center', width: 'auto' },
                { label: '物料名称', prop: 'materielName', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierCode', align: 'center', width: 'auto' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: 'auto' },
                { label: '报价', prop: 'taxFreePrice', align: 'center', width: 'auto' },
                { label: '是否定价', prop: 'isFixed', align: 'center', width: 'auto' },
                { label: '物料分类', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '报价单号', prop: 'quotationNo', align: 'center', width: 'auto' }
            ],
        }
    },
    computed: {
        // ...mapState({
        //     dt_materiel_classify: state => state.dict.dt_materiel_classify,
        //     dt_materiel_group: state => state.dict.dt_materiel_group,
        //     dt_materiel_type: state => state.dict.dt_materiel_type,
        // })
    },
    mounted () {
        this.getList()
        // this.$store.dispatch('dict/getDicData', ['dt_materiel_classify', 'dt_materiel_group', 'dt_materiel_type'])
    },
    methods: {
        checked (i) {
            this.$refs.tbselect.toggleRowSelection(this.list[i], true)
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                console.log(res, 'yyyy')
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.materielCode === items.materielCode) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        getData (val) {
            console.log(val, '99')
            // this.listQuery.orgCode = val[0].orgCode
            this.$set(this.listQuery, 'orgCode', val[0].organizationCode)
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        },
        //选择确认
        handleConfirm () {
            this.$emit('handleDialogClick', this.listSelected)
            this.dialogVisible = false
        },
        handlePreview () {
            console.log('预览')
            this.$emit('handlePre', 'quote', this.listSelected)
        }
    }
};
