import * as api from "@/api/project/srm/materialClassify/orgmaterialParameters"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogWlxzbyzsj from '@/components/Dialog/dialogMaterial.vue'//主数据物料选择
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'//物料分类
import throttle from 'lodash/throttle'

export default {
    components: { caiGou, dialogPersonSelect, dialogWlxzbyzsj, dialogMaterielCategory },
    data () {
        return {
            dialogStatus: "",
            dialogVisible: false,
            person: "",
            DICT_CODE: DICT_CODE,
            dialogPersonVisible: false,
            titlePerson: "",
            temp: {
                rowId: undefined
            },
            rules: {
                orgCode: [{ required: true, message: '组织编码不能为空', trigger: 'blur' }],
                parentRowId: [{ required: true, message: '上级分类编码不能为空', trigger: 'blur' }],
                classCode: [{ required: true, message: '分类编码不能为空', trigger: 'blur' }],
                className: [{ required: true, message: '分类名称不能为空', trigger: 'blur' }],
            }
        }
    },
    computed: {
        ...mapState({
            dt_manufacture_diff: state => state.dict.dt_manufacture_diff,
            dt_srm_pdy: state => state.dict.dt_srm_pdy,
            dt_unit_type: state => state.dict.dt_unit_type,
            dt_timeunit: state => state.dict.dt_timeunit,
            dt_srm_pdz: state => state.dict.dt_srm_pdz,
            dt_srm_pc0: state => state.dict.dt_srm_pc0,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_manufacture_diff", 'dt_srm_pdy', 'dt_unit_type', 'dt_timeunit', 'dt_srm_pdz', 'dt_srm_pc0'])
    },
    methods: {
        //物料选择
        handleMaterialCode () {
            this.$refs.refZsjWlxz.dialogVisible = true
        },
        //物料选择确定
        confirmMateriel (data) {
            console.log(data, 'bbbvvv')
            this.$set(this.temp, 'materialCode', data[0].materielCode)
            this.$set(this.temp, 'materialName', data[0].materielName)
            this.$set(this.temp, 'materielEngName', data[0].materielEngName)//物料英文名
            this.$set(this.temp, 'materielType', data[0].materielType)//物料类型
            this.$set(this.temp, 'materielTypeName', data[0].materielTypeName)//物料类型名称
            this.$set(this.temp, 'materielGroup', data[0].materielGroup)//物料组
            this.$set(this.temp, 'materielGroupName', data[0].materielGroupName)//物料组名称
        },
        //物料分类
        handleClassifyCode () {
            if (this.temp.purchaseOrg) {
                this.$refs.wlfl.listQuery.orgCode = this.temp.purchaseOrg
                this.$refs.wlfl.selectedRows = []
                this.$refs.wlfl.handleQuery()
                this.$refs.wlfl.dialogVisible = true
            } else {
                this.$message({ type: 'warning', message: '请先选择采购组织' })
            }
        },
        //物料分类选择确定
        confirmClassify (data) {
            this.$set(this.temp, 'materialClassification', data[0].classCode)
            this.$set(this.temp, 'materialClassificationName', data[0].className)
        },
        //人员选择
        handlePersonCode (val) {
            this.person = val
            if (this.person == 'jhPerson') {
                this.titlePerson = '采购计划员'
            } else if (this.person == 'glPerson') {
                this.titlePerson = '采购管理员'
            } else {
                this.titlePerson = '商务人员'
            }
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            console.log(val[0].employeeName)
            if (this.titlePerson === '采购计划员') {
                this.$set(this.temp, 'purchasePlanner', val[0].employeeCode)
                this.$set(this.temp, 'purchasePlannerName', val[0].employeeName)
            }
            if (this.titlePerson === '采购管理员') {
                this.$set(this.temp, 'purchaseManager', val[0].employeeCode)
                this.$set(this.temp, 'purchaseManagerName', val[0].employeeName)
            }
            if (this.titlePerson === '商务人员') {
                this.$set(this.temp, 'purchaseBusiness', val[0].employeeCode)
                this.$set(this.temp, 'purchaseBusinessName', val[0].employeeName)
            }

        },
        //获取组织编码名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //获取采购组名称
        selectorgGroupName (val) {
            this.temp.purchaseGroupName = val
        },
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        updateData: throttle(function () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        listSelected (val) {
            this.temp.parentRowId = val[0].parentRowId
            this.$refs.classify.dialogCodeVisible = false
        },
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                materialCode: undefined,
                materialName: undefined,
                makeDistinction: undefined,
                purchaseManagerName: undefined,
                purchaseGroup: undefined,
                purchasePlannerName: undefined,
                purchaseBusinessName: undefined,
                materialClassificationName: undefined,
                targetPrice: undefined,
                purchaseClassification: undefined,
                purchaseUmo: undefined,
                basicUmo: undefined,
                purchaseMeasurementDistribution: undefined,
                basicMeasurementDistribution: undefined,
                purchaseSnp: undefined,
                purchaseLeadTime: undefined,
                eoq: undefined,
                leadTimeUnit: undefined,
                purchaseTriggeringMethod: undefined,
                purchaseStrategy: undefined,
            }
        }
    }
}
