import * as api from '@/api/project/mes/dataBase/processStep'
import Pagination from '@/components/Pagination'
import { DICT_CODE } from '@/utils/constant'

export default {
    name: 'processStep',
    components: { Pagination },
    data() {
        return {
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                currentPage: 1,
                pageSize: 10
            },
            DICT_CODE: DICT_CODE,
            selectedRows: null, //多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created() {},
    filters: {
        isEnableFilter: function(e) {
            if (e === 'step_1') {
                return '是'
            } else {
                return '否'
            }
        }
    },
    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange(val) {
            this.selectedRows = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$emit('handSelectSelectRow', this.selectedRows)
        }
    }
}
