import * as api from '@/api/project/mes/dataBase/equipmentLedger'
import Pagination from '@/components/Pagination'
import { DICT_CODE } from '@/utils/constant'
import { mapState } from 'vuex'

export default {
    name: 'supplier',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                supplierCode: undefined,
                supplierName: undefined,
                supplierType: undefined
            },

            supplierCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            importDialogVisible: false,
            dialogStatus: '',
            tableKey: 0,
            DICT_CODE: DICT_CODE
        }
    },
    computed: {
        ...mapState({
            dt_supplier_type: state => state.mesDict.dt_supplier_type
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', ['dt_supplier_type'])
        this.getList()
    },
    methods: {
        getList() {
            api.querySuppierRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getList()
        },
        selectRow(val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$emit('handSelectSupplierRows', this.selectlistRow)
        }
    }
}
