import DAInfo from "./DAInfo"
import GAInfo from "./GAInfo"
import mainBox from "./main"
import getPageTitle from '@/utils/getPageName'
import { queryProjects } from "@/api/project/iot/IOT/project"
import { queryProjectCodes as queryDevice } from "@/api/project/iot/IOT/em"
import { queryGateways as queryAateway } from "@/api/project/iot/IOT/ga"

export default {
    name: "alarmCenter",
    data () {
        return {
            pages: 'main',
            code: undefined, //一级页面项目编码
            row: undefined,
            title: getPageTitle(this.$route.name),
            projectList: null,
            deviceList: null,
            gatewayList: null,
            listQuery: {
                alarmStartTime: undefined,
                alarmEndTime: undefined
            },
        }
    },
    components: { mainBox, DAInfo, GAInfo },
    created () {
        this.getList()
    },
    methods: {
        setPage (pages) {
            this.pages = pages
        },
        setCode (code) {
            this.code = code
        },
        setRow (row) {
            this.row = row
        },
        setTime (listQuery) {
            this.listQuery = listQuery
        },
        setDevice (val) {
            queryDevice(val).then(res => {
                this.deviceList = res.data
            })
        },
        setGateway (val) {
            queryAateway(val).then(res => {
                this.gatewayList = res.data
            })
        },
        getList () {
            let listQuery = {
                page: true,
                currentPage: 1,
                pageSize: 1000,
            }
            queryProjects(listQuery).then(res => {
                this.projectList = res.data.list
            })

        }
    },
};
