import * as api from "@/api/project/srm/priceManage/priceConfirmList"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择

export default {
    name: "priceFeedback",
    components: { Pagination, caiGou, dialogGysxz },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            subList: [],
            total: 0,
            totalSub: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                state: 'P003'
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: [0, 1], //表格索引
            rangeDate: null
        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pc6: state => state.dict.dt_srm_pc6,
            dt_srm_pdt: state => state.dict.dt_srm_pdt,
        })
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.createTimeBegin = value[0]
                this.listQuery.createTimeEnd = value[1]
            } else {
                this.listQuery.createTimeBegin = undefined
                this.listQuery.createTimeEnd = undefined
            }
        }
    },
    filters: {
        stateFilter: function (a) {
            if (a) {
                if (a == '0') {
                    return '不通过'
                }
                if (a == '1') {
                    return '通过'
                }
            }
        }
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pc6', 'dt_srm_pdt'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() / 2
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商选择确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_p0, this.dt_srm_pc6, this.dt_srm_pdt]
                res.data.list = this.$myFun.codeToName(res.data.list, options, ['state', 'documentType', 'settlementMethod'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        getsubList () {
            api.queryRecordsItems(this.selectlistRow.confirmationNo).then(res => {
                this.subList = res.data.map(v => {
                    v.replyPrice = v.replyPrice ? v.replyPrice : v.requirePrice
                    return v
                })
            })
        },
        //反馈按钮单击事件
        handleFeedback () {
            if (this.selectlistRow) {
                this.$confirm("是否反馈？", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let dto = {
                        method: 'feedback',
                        rowIds: [this.selectlistRow.rowId]
                    }
                    api.submitRecords(dto).then(() => {
                        this.$message({
                            title: '成功',
                            message: '反馈成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.subList = []
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //通过按钮单击事件
        handleAdopt () {
            if (this.selectlistRowSub && this.selectlistRowSub.length > 0) {
                this.subList.forEach((v, index) => {
                    this.selectlistRowSub.forEach(val => {
                        if (val.rowId == v.rowId) {
                            this.$set(this.subList[index], 'confirmState', '1')
                        }
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        //不通过按钮单击事件
        handleNotAdopt () {
            if (this.selectlistRowSub && this.selectlistRowSub.length > 0) {
                this.subList.forEach((v, index) => {
                    this.selectlistRowSub.forEach(val => {
                        if (val.rowId == v.rowId) {
                            this.$set(this.subList[index], 'confirmState', '0')
                        }
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        //保存按钮单击事件
        handleSave () {
            if (this.selectlistRowSub && this.selectlistRowSub.length > 0) {
                let result = this.selectlistRowSub.every(v => {
                    return v.confirmState && v.replyPrice !== ""
                })
                if (result) {
                    this.$confirm("是否保存？", "提示", {
                        confirmButtonText: "确定",
                        cancelButtonText: "取消",
                        type: "warning"
                    }).then(() => {
                        let dto = this.selectlistRowSub.map(v => {
                            let dto = {
                                confirmDes: v.confirmDes,
                                confirmState: v.confirmState,
                                confirmationNo: this.selectlistRow.confirmationNo,
                                replyPrice: v.replyPrice,
                                rowId: v.rowId
                            }
                            return dto
                        })
                        api.saveRecords(dto).then(() => {
                            this.$message({
                                title: '成功',
                                message: '保存成功',
                                type: 'success',
                                duration: 2000
                            })
                            this.getList()
                        })
                    }).catch(() => {
                        this.$message({
                            type: "info",
                            message: "已取消"
                        })
                    })

                } else {
                    this.$message({
                        title: "警告",
                        message: "请填写确认状态及回复价格",
                        type: "warning"
                    })
                }

            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.subList = []
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getsubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        selectChangeSub (val) {
            this.selectlistRowSub = val
        },
        // rowClickSub (val) {
        //     // this.$refs.tbSub.clearSelection()
        //     this.$refs.tbSub.toggleRowSelection(val)
        // },
    }
};
