import * as api from '@/api/project/ems/dataBase/productPlan.js'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'
export default {
    name: 'productPlan',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                productCode: undefined
            },
            temp: {
                productCode: undefined,
                productName: undefined,
                yield: undefined,
                outputValue: undefined,
                productStartTime: undefined,
                productEndTime: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            rules: {
                productCode: [
                    {
                        required: true,
                        message: '请选择产品',
                        trigger: 'blur'
                    }
                ],
                yield: [
                    {
                        required: true,
                        message: '产量不能为空',
                        trigger: 'blur'
                    }
                ],
                outputValue: [
                    {
                        required: true,
                        message: '产值不能为空',
                        trigger: 'blur'
                    }
                ],
                productEndTime: [
                    {
                        required: true,
                        message: '结束时间不能为空',
                        trigger: 'blur'
                    }
                ],
                productStartTime: [
                    {
                        required: true,
                        message: '开始时间不能为空',
                        trigger: 'blur'
                    }
                ]
            },
            pickerOptionsStart: {
                disabledDate: time => {
                    let endDateVal = this.temp.productEndtTime
                    if (endDateVal) {
                        return time.getTime() > new Date(endDateVal).getTime()
                    }
                }
            },
            pickerOptionsEnd: {
                disabledDate: time => {
                    let beginDateVal = this.temp.productStartTime
                    if (beginDateVal) {
                        return time.getTime() < new Date(beginDateVal).getTime()
                    }
                }
            }
        }
    },
    computed: {
        ...mapState({
            dt_cp_product_list: state =>
                state.businessComponent.dt_cp_product_list
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_cp_product_list'
        ])
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    destroyed () {
        this.setTableHeight()
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_cp_product_list'
        ])
        this.getList()
    },
    methods: {
        getList () {
            api.getDTOs(this.listQuery).then(res => {
                res.data.list.map((v, i) => {
                    v.serial = i + 1
                })
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },
        handleUpdate () {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.readonly = true //员工编码不可以编写
                this.temp = this.selectlistRow[0] // copy obj
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                //编辑
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        let arr = this.dt_cp_product_list.options.filter(
                            item => {
                                return item.productCode == this.temp.productCode
                            }
                        )
                        this.temp.productName = arr[0].productName
                        api.patchDTO(this.temp.rowId, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDeleteBatch () {
            if (this.selectCode.length == 0) {
                this.$message.warning('请勾选记录！')
                return
            }
            this.delete(this.selectCode)
        },
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.rowId
                        })
                        api.deleteDTO(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        let arr = this.dt_cp_product_list.options.filter(
                            item => {
                                return item.productCode == this.temp.productCode
                            }
                        )
                        this.temp.productName = arr[0].productName
                        api.postDTO(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        cancel () {
            this.dialogFormVisible = false
        },
        resetTemp () {
            this.temp = {
                productCode: undefined,
                productName: undefined,
                yield: undefined,
                outputValue: undefined,
                productStartTime: undefined,
                productEndTime: undefined,
                remark: undefined
            }
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.currentPage = 1
            this.listQuery.productCode = undefined
            this.getList()
        },
        handleClose () {
            this.resetTemp()
        },
        handleSelectionChange (selection) {
            this.selectCode = selection.map(v => v.productCode)
        },
        // 时间开始选择器
        startTimeStatus: function (value) {
            this.temp.productStartTime = value
            if (value > this.temp.productEndTime) {
                this.temp.productEndTime = undefined
            }
        }
    }
}
