import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/productReturn/uninstructedReturn"
import { mapState } from "vuex"
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import wlxzbykcz from '@/components/Select/wlxzbykcz'//库存帐物料信息模块
import qrcode from "@/components/QRCode"
import workCenterSelect from "@/components/Select/work-center-select.vue" //下拉组件


export default {
    name: "uninstructedReturn",
    components: { Pagination, wlxzbykcz, AuthoType, workCenterSelect, qrcode },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            options: [],
            // value: "",
            unMutiple: false,
            value3: "",
            areaList: null,
            locatorList: [],
            list: [],
            list1: null,
            list2: null,
            list3: null,
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            activeName2: "first",
            isEdit: false,
            disabled: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                arrivalOrgCode: "",
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: this.timeFormat(),
                toWarehouseCode: undefined,
                workshopCode: undefined
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            storesTransfersDTO: {
                accountDate: undefined,
                businessType: undefined,
                businessTypeName: undefined,
                orgCode: undefined,
                receiveOrderItemDTOs: [],
                receiveOrderNumber: undefined,
                sourceOrderNumber: undefined,
                supplierCode: undefined,
                supplierName: undefined,
                thirdWarehouseCode: undefined,
                warehouseCode: undefined,
                warehouseName: undefined
            },
            temp: {
                orgCode: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                accountDate: undefined,
                toWarehouseCode: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                receiveOrderNumber: undefined,
            },
            printData: {},//打印数据          
            listQuery2: {
                proofNo: undefined,
                currentPage: 1,
                pageSize: 10
            },
            orgCode: undefined,
            warehouseCode: undefined,
            dialogStatus: "",
            wlxzRows: null,
            wlxzDialogFormVisible: false,
            downloadLoading: false,
            dialogPrintVisible: false,
            tableKey: [0, 1, 2],
        }
    },
    computed: {
        ...mapState({
            dt_wzs_type_screenGroupNum: state => state.businessComponent.dt_wzs_type_screenGroupNum,
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_wzs_type_screenGroupNum"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 30
            this.pheight = this.$myFun.getSingleTbHeight() + 55
            this.sheight = this.$myFun.getSingleTbHeight() - 70
        },
        getLinecode (val) {
            this.listQuery.lineCode = val[0].workCenterCode
            this.listQuery.workshopCode = val[0].feedArea
        },
        queryworkshop (v) { //查询车间仓库
            this.listQuery.workshopCode = v[0].feedArea
        },
        queryworkshop1 (v) { //查询车间仓库
            this.listQueryProofMain.workshopCode = v[0].feedArea
        },
        handleQuery1 () {
            this.listQueryProofMain.currentPage = 1
            this.getList1()
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        getList1 () {//查询凭证主表
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.list3 = []
            api.queryRecords1(this.listQueryProofMain).then(res => {
                res = res.data
                this.list2 = res.list
                // this.total = res.pages.count;
            })
        },
        getListSub () {//查询凭证明细
            this.listQueryProofSub.receiveOrderNumber = this.selectlistRowout.receiveOrderNumber
            api.queryRecords(this.listQueryProofSub).then(res => {
                res = res.data
                this.list3 = res.list
                this.total3 = res.pages.count
            })
        },
        selectRow1 (val) {
            this.selectRowData1 = val
        },
        handleCreate () {//新增弹窗
            if (this.listQuery.orgCode && this.listQuery.accountDate && this.listQuery.warehouseCode && this.listQuery.businessType && this.listQuery.lineCode) {
                this.wlxzDialogFormVisible = true
                this.$nextTick(() => {
                    this.$refs.wlxzRef.listSelected = [...this.list]
                    this.$refs.wlxzRef.isEdit = true
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择查询条件",
                    type: "warning"
                })
            }
            api.getArea(this.listQuery.warehouseCode).then(resa => {
                resa = resa.data
                this.areaList = resa
            })
        },
        savecheck () {
            let result = true
            let b = this.list.map(v => {
                v.sourceOrderNumber = '*'
                v.accountDate = this.listQuery.accountDate
                // v.measuringUnitName=this.selectRowData1.measuringUnitName
                v.toWareAreaCode = '*'
                v.toWareLocationCode = '*'
                v.supplierCode = '*'
                v.sourceRowNo = 1
                return v
            })
            b = this.list.filter(v => {
                return v.receiveQuantity !== 0
            })
            b.forEach((v) => {
                if (!v.wareAreaCode || !v.wareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    })
                    result = false
                }
            })
            // this.save=b
            if (result) {
                return b
            } else {
                return false
            }

        },
        handleUpdate () {
            let result = true
            let saveList = []
            let b = this.list.map(v => {
                v.sourceOrderNumber = '*'
                v.accountDate = this.listQuery.accountDate
                // v.measuringUnitName=this.selectRowData1.measuringUnitName
                v.toWareAreaCode = '*'
                v.toWareLocationCode = '*'
                v.supplierCode = '*'
                v.sourceRowNo = 1
                return v
            })
            b.forEach(item => {
                if (item.receiveQuantity > 0) {
                    result = false
                    saveList.push(item)
                }
            })
            if (result) {
                this.$message({
                    title: "警告",
                    message: "退料数量不可全部为0或空",
                    type: "warning"
                })
            } else {
                this.storesTransfersDTO.orgCode = this.listQuery.orgCode
                this.storesTransfersDTO.businessType = this.listQuery.businessType
                this.storesTransfersDTO.accountDate = this.listQuery.accountDate
                this.storesTransfersDTO.warehouseCode = this.listQuery.warehouseCode
                this.storesTransfersDTO.supplierCode = this.listQuery.workshopCode
                this.storesTransfersDTO.sourceOrderNumber = '*'
                this.storesTransfersDTO.receiveOrderItemDTOs = saveList
                api.addRecords(this.storesTransfersDTO).then(() => {
                    this.disabled = true
                    this.$message({
                        title: "成功",
                        message: "保存成功",
                        type: "success",
                        duration: 2000
                    })
                })
            }



        },
        update () {
            if (this.$refs.wlxzRef.listSelected.length > 0) {
                this.list = this.$refs.wlxzRef.listSelected
                this.wlxzDialogFormVisible = false
                this.isEdit = true
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleDelete () {//删除列表
            if (this.selectRowData1 && this.selectRowData1.length == 1) {
                this.$confirm("此操作将删除所选中数据, 是否继续?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                })
                    .then(() => {
                        for (let i = 0; i < this.list.length; i++) {
                            if (
                                this.list[i].materielCode ===
                                this.selectRowData1[0].materielCode
                            ) {
                                this.list.splice(i, 1)
                                this.$message({
                                    title: "成功",
                                    message: "删除成功",
                                    type: "success",
                                    duration: 2000
                                })
                            }
                        }
                    })
                    .catch(() => {
                        this.$message({
                            type: "info",
                            message: "已取消删除"
                        })
                    })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handelRefresh () { //刷新
            this.isEdit = false
            this.disabled = false
            this.list = []
            this.listQuery = []
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowout) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowout
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.receiveOrderNumber)
                })

            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        handleDownload () { //导出
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '打印次数', '凭证号', '业务类型',
                    '物料编码', '物料名称', '数量',
                    '退料产线', '退料仓库', '组织编码',
                    '退回仓库', '区位', '储位',
                    '记账日期', '创建日期', '记账人',
                    '凭证行', '指示号', '指示行'
                ]
                const filterVal = [
                    'dayin', 'receiveOrderNumber', 'dicType',
                    'materielCode', 'materielName', 'receiveQuantity',
                    'lineCode', 'warehouseCode', 'orgCode',
                    'warehouseCode', 'wareAreaCode',
                    'wareLocationCode', 'accountDate', 'createTime',
                    'creator', 'sourceOrderCode', 'sourceRowNo',
                ]
                const data = this.formatJson(filterVal, this.list3)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        handleFilter1 () {
            if (this.listQueryProofMain.businessType) {
                // this.workshopCode = this.listQuery1.workshopCode
                this.getList1()
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择查询条件",
                    type: "warning"
                })
            }
        },
        selectRowout (val) {//出库凭证复选框选中方法
            this.selectlistRowout = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRowout)
            if (this.selectlistRowout) {
                this.getListSub()
            }
        },
        selectRowout1 (val) {//出库凭证单击行选中方法
            this.selectlistRowout1 = val
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        selectRowchange (val) {
            // this.selectlistRowchange = val;
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_wzs_type_screenGroupNum.options[0].wareRuleCode
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_wzs_type_screenGroupNum.options[0].wareRuleCode
                this.getList1()
            }
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.locatorList, i, res)
                this.$set(this.list[i], 'wareLocationCode', res[0].locationCode)
                this.$set(this.list, i, this.list[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.listQuery.warehouseCode, areaCode, i)
        }
    }
};
