import * as api from '@/api/project/eas/database/groups'
import Pagination from '@/components/Pagination'
export default {
    name: 'teamEmployeeSelect',
    components: { Pagination },
    data () {
        return {
            list: [],
            tableKey: [0],
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            temp: {
                classCode: undefined,
                description: undefined,
                address: undefined,
                deviceCode: undefined,
                deviceName: undefined,
                faultCode: undefined,
                faultGrade: undefined,
                faultTypeCode: undefined,
                happenTime: undefined,
                ifMachineHalt: undefined,
                machineHaltTime: undefined,
                mobile: undefined,
                operator: undefined,
                operatorName: undefined,
                partCode: undefined
            }
        }
    },
    props: {
        teamCode: {
            type: String,
            default: undefined
        }
    },
    created () {
        this.getList()
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        //查询主表
        getList () {
            api.queryTeamEmployee(this.teamCode, this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val[0]
            if (val.length > 1) {
                this.$refs.mainList.clearSelection()
                this.$refs.mainList.toggleRowSelection(val.pop())
            }
            this.$emit('change', this.selectlistRow)
        },
        //主表行点击
        showSubTable (row) {
            this.$refs.mainList.clearSelection()
            this.$refs.mainList.toggleRowSelection(row)
        }
    }
}
