import * as api from "@/api/project/srm/quotaManagement/quotaApplication"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { mapState } from "vuex"
import dialogOrgSelect from '@/components/Dialog/dialogOrgSelect'
import dialogWlxzbycglx from '@/components/Dialog/dialogWlxzbycglx.vue'//物料选择
export default {
    components: { Pagination, caiGou, dialogGysxz, dialogOrgSelect, dialogWlxzbycglx },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogVisible: false,
            dialogStatus: '',
            list: [],
            listSub: [],
            total: 0,
            temp: {
                rowId: undefined,
                applicationNo: undefined,
                orgCode: undefined,
                firstExecuteTime: undefined,
                applicationCompanyName: undefined,
                applicant: undefined,
                endExecuteTime: undefined,
                mainContent: undefined,
                adjustType: undefined,
                changeCause: undefined
            },
            status: false,
            statusList: [],
            radio: {
                value: '1',
            },
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgName: undefined,
                orgCode: undefined
            },
            tableKey: [0, 1]//表格索引，虚拟dom渲染表格时使用
        }
    },
    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_pc2: state => state.dict.dt_srm_pc2,
            dt_srm_paf: state => state.dict.dt_srm_paf,
            dt_srm_pag: state => state.dict.dt_srm_pag
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_pc2', 'dt_srm_paf', 'dt_srm_pag'])
        this.getList()
    },
    watch: {
        'listQuery.orgCode' (val) {
            if (val) {
                this.getList()
            }
        },
    },
    methods: {
        handleClose () {
            // this.$refs.dataForm.resetFields()
            this.list = []
        },
        initForm () {
            this.temp = {
                applicationNo: undefined,
                orgCode: undefined,
                firstExecuteTime: undefined,
                applicationCompanyName: undefined,
                applicant: undefined,
                endExecuteTime: undefined,
                mainContent: undefined,
                adjustType: undefined,
                changeCause: undefined
            }
        },
        getData (val) {
            console.log(val, 'hhh')
            this.listQuery.orgCode = val[0].organizationCode
        },
        //获取采购组织名称
        selectName (val) {
            this.temp.orgName = val
        },
        //申请单位
        showOrg () {
            this.$refs.refOrg.dialogVisible = true
        },
        //申请单位确定
        confirmOrg (data) {
            console.log(data)
            if (data) {
                this.$set(this.temp, 'applicationCompanyCode', data[0].organizationCode)
                this.$set(this.temp, 'applicationCompanyName', data[0].organizationName)
            }
        },
        //人员选择
        showPerson () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            this.$set(this.temp, 'applicantCode', val[0].employeeCode)
            this.$set(this.temp, 'applicant', val[0].employeeName)
        },
        //供应商
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商确定
        handleGysClick (val) {
            this.$set(this.listQuery, 'supplierName', val[0].supplierName)
            this.$set(this.listQuery, 'supplierCode', val[0].supplierCode)
        },
        showWl () {
            this.$refs.wlxz.dialogVisible = true
            this.$refs.wlxz.getList()
        },
        confirmWlxz (data) {
            console.log(data)
            if (data) {
                this.$set(this.listQuery, 'materialCode', data[0].materialCode)
                this.$set(this.listQuery, 'materialName', data[0].materialName)
            }
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        //显示主表
        getList () {
            if (this.listQuery.orgCode) {
                api.queryRecords(this.listQuery).then(res => {
                    let options = [this.dt_srm_pc2, this.dt_srm_p0, this.dt_srm_paf, this.dt_srm_pag]
                    res.data.list = this.$myFun.codeToName(res.data.list, options, ['adjustType', 'state', 'auditState', 'auditConclusion'])
                    this.list = res.data.list
                    this.total = res.data.pages.count
                    this.$nextTick(function () {
                        this.checked()
                    })
                })
            }
        },
        //显示从表
        getSubList () {
            let dto = {
                applicationNo: this.selectlistRow.applicationNo,
                currentPage: 1,
                pageSize: 10
            }
            api.queryRecordsItem(dto).then(res => {
                console.log(res, 'uuu')
                this.listSub = res.data
            })
        },
        createData () {
            if (this.selectlistRow) {
                console.log(this.listSub, 'ppppp')
                this.$emit('handleConfirmSearch', this.listSub.map(v => {
                    v.orgCode = this.selectlistRow.orgCode
                    v.state = this.selectlistRow.state
                    return v
                }))
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
}
