import * as api from "@/api/project/spm/sparePartsPurchase/requirementsGathering"
import { DICT_CODE } from '@/utils/constant'
import Pagination from "@/components/Pagination"
import orgSelect from '@/components/Select/orgSelect'
import employeesSelect from '@/components/Select/employeesSelect' //下拉组件
import { mapState } from 'vuex' // 基本数据引入
// dt_requisitions_type
export default {
    name: "getSummary",
    components: { Pagination, orgSelect, employeesSelect },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_requisitions_type: state => state.dict.dt_requisitions_type
        })
    },
    data () {
        return {
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 50,
                state: 'srs20',
                ifSum: 'N'
            },
            employeeDialogVisible: false,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_requisitions_type'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.createTimeStart = value[0]
                this.listQuery.createTimeEnd = value[1]
            } else {
                this.listQuery.createTimeStart = undefined
                this.listQuery.createTimeEnd = undefined
            }
        },
        'dt_requisitions_type' () {
            if (this.list) {
                this.list = this.$myFun.srmCodeToName(
                    JSON.parse(JSON.stringify(this.list)),
                    [this.dt_requisitions_type],
                    ['requisitionsType']
                )
            }
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                if (this.dt_requisitions_type.length) {
                    this.list = this.$myFun.srmCodeToName(
                        res.data.list,
                        [this.dt_requisitions_type],
                        ['requisitionsType']
                    )
                }
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 选择人员
        handleEmployee (val) {
            this.listQuery.requisitionerCode = val ? val.employeeCode : undefined
        },
        //汇总按钮单击事件方法
        handleTotal () {
            if (this.selectlistRow && this.selectlistRow.length > 1) {
                this.$confirm('此操作将进行汇总, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                }).then(() => {
                    let rowIds = []
                    this.selectlistRow.forEach((val, index) => {
                        rowIds[index] = val.rowId
                    })
                    api.addTotal(rowIds).then(() => {
                        this.$message({
                            title: '成功',
                            message: '汇总成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消汇总'
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请至少勾选两行信息",
                    type: "warning"
                })
            }
        },
        //全部汇总按钮单击事件
        handleAllTotal () {
            this.$confirm('此操作将所有信息进行汇总, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            }).then(() => {
                let rowIds = []
                this.list.forEach((val, index) => {
                    rowIds[index] = val.rowId
                })
                api.addTotal(rowIds).then(() => {
                    this.$message({
                        title: '成功',
                        message: '汇总成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: 'info',
                    message: '已取消全部汇总'
                })
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
