import * as api from '@/api/project/ems/deviceManagement/equipmentModel.js'
import Pagination from '@/components/Pagination'
import throttle from 'lodash/throttle'

export default {
    name: 'equipmentModel',
    components: { Pagination },
    data () {
        return {
            list: null,
            kindList: null,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                meterTypeName: undefined,
                meterCode: undefined
            },
            temp: {
                meterTypeCode: undefined,
                meterTypeName: undefined,
                meterCode: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            total: 0,
            theight: 0, //表格高度
            selectlistRow: null,
            rules: {
                meterCode: [
                    {
                        required: true,
                        message: '请选择设备类别',
                        trigger: 'blur'
                    }
                ],
                meterTypeCode: [
                    {
                        required: true,
                        message: '设备型号编码不能为空',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                meterTypeName: [
                    {
                        required: true,
                        message: '设备型号名称不能为空',
                        trigger: 'blur'
                    },
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    mounted () {
        this.setTableHeight()
        this.getList()
        this.getKindList()
    },
    destroyed () {
        this.setTableHeight()
        this.getList()
        this.getKindList()
    },
    methods: {
        getList () {
            api.queryMeterType(this.listQuery).then(res => {
                res.data.list.map((v, i) => {
                    v.serial = i + 1
                })
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        getKindList () {
            api.queryKindList().then(res => {
                this.kindList = res.data
            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        api.createMeterType(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate () {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.temp = JSON.parse(JSON.stringify(this.selectlistRow[0])) // copy obj
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                //编辑
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        api.updateMeterType(
                            this.temp.meterTypeCode,
                            this.temp
                        ).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.meterTypeCode
                        })
                        api.deleteMeterType(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.currentPage = 1
            this.listQuery.meterTypeName = undefined
            this.listQuery.meterCode = undefined
            this.getList()
        },
        resetTemp () {
            this.temp = {}
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        setTableHeight () {
            //表格高度计算
            this.theight = this.$myFun.getSingleTbHeight()
        },
        selectRow (val) {
            // 获取表格选中时的数据
            this.selectlistRow = val
        }
    }
}
