import * as api from "@/api/project/mes/dataBase/equipmentType";
import Pagination from "@/components/Pagination";
import { mapState } from "vuex";
export default {
    name: "equipmentType",
    components: { Pagination },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                equipTypeCode: undefined,
                equipTypeName: undefined,
                note: undefined,
            },
            dialogFormVisible: false,
            dialogStatus: "",
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        };
    },
    created () { },
    computed: {
        ...mapState({
            dt_role_type: state => state.mesDict.dt_role_type
        })
    },
    mounted () {
        this.$store.dispatch('mesDict/getDicData', ["dt_role_type"]);
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight();
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1;
            this.getList();
        },
        handleClose () {
            this.resetTemp();
            this.$refs.temp.resetFields();
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val;
            this.getList();
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val;
            this.getList();
        },
        resetTemp () {
            this.temp = {
                equipTypeCode: undefined,
                equipTypeName: undefined,
                note: undefined,
            }
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.resetTemp();
            this.dialogStatus = "create";
            this.dialogFormVisible = true;
        },
        create () {
            api.addRecord(this.temp).then(() => {
                this.getList();
                this.dialogFormVisible = false;
                this.$message({
                    title: "成功",
                    message: "新增成功",
                    type: "success",
                    duration: 2000
                });
            });

        },
        handleUpdate () {
            //编辑按钮单击事件方法
            if (this.selectedRows && this.selectedRows.length == 1) {
                this.temp = this.selectedRows[0]
                api.getRecord(this.temp.equipTypeCode).then(res => {
                    this.temp = res.data;
                    this.dialogStatus = "update";
                    this.dialogFormVisible = true;
                });
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }

        },
        update () {
            //编辑确认
            this.$refs["temp"].validate(valid => {
                if (valid) {
                    api.updataRecord(this.temp.equipTypeCode, this.temp).then(() => {
                        this.getList();
                        this.dialogFormVisible = false;
                        this.$message({
                            title: "成功",
                            message: "更新成功",
                            type: "success",
                            duration: 2000
                        });
                    }
                    );
                }
            });
        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectedRows && this.selectedRows.length == 1) {
                this.$confirm("此操作将永久删除该文件, 是否继续?", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    let equipTypeCodeList = [];
                    this.selectedRows.forEach((val, index) => {
                        equipTypeCodeList[index] = val.equipTypeCode;
                    });
                    api.deleteRecord(equipTypeCodeList).then(() => {
                        this.getList(),
                            this.$message({
                                title: "成功",
                                message: "删除成功",
                                type: "success",
                                duration: 2000
                            });
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消删除"
                    });
                });
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectedRows = val;
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, "selected"); //单击行绑定点击事件
        }
    }
};
