import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'wlxzbykcz',
    components: { Pagination },
    props: ['orgCode', 'unMutiple', 'stockType', 'warehouseCode'],
    data () {
        return {
            list: null,
            total: 0,
            areaList: null,
            spanArr: [],
            locatorList: null,
            stockStateArr: [],
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                materielCode: undefined,
                materielName: undefined,
                orgCode: this.orgCode,
                warehouseCode: this.warehouseCode,
                stockType: this.stockType,
                stockState: this.stockState
            },
            activeName2: 'first',
            listSelected: [],
            materielCode: undefined,
            selectCode: [],
            tableKey: 0,
            employeeType: undefined,
            isEdit: false,
            isUnselected: false
        }
    },
    computed: {
        ...mapState({
            dt_stock_type: state => state.dict.dt_stock_type,
            dt_stock_status: state => state.dict.dt_stock_status,
            dt_materiel_type: state => state.dict.dt_materiel_type,
        })
    },
    mounted () {
        // this.$store.dispatch('dict/getDicData', ['dt_stock_type', 'dt_stock_status', 'dt_materiel_type', 'dt_stock_type']);
        this.getList()
    },
    methods: {
        getList () {
            this.list = [{
                orgCode: 'XX采购组织', itemCode: 'XM-052001', itemName: 'X7研发项目',
                year: '2019', itemType: '研发', platform: 'X7', leader: '胡三'
            }, {
                orgCode: 'XX采购组织1', itemCode: 'XM-052001', itemName: 'X7研发项目',
                year: '2019', itemType: '研发', platform: 'X7', leader: '胡三'
            }]
            if (this.listSelected) {
                this.listSelected.forEach(items => {
                    this.list.forEach((item, index) => {
                        if (item.rowId === items.rowId) {
                            this.$nextTick(function () {
                                this.checked(index)
                            })
                        }
                    })
                })
            }

        },
        checked (i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.rowId === row.rowId) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.unMutiple) {
                    if (val.length == 1) {
                        this.listSelected = val
                    }
                    else {
                        this.$message({
                            title: "警告",
                            message: "请选择一条信息",
                            type: "warning"
                        })
                    }
                } else {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.rowId === items.rowId) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.rowId !== row.rowId
                })
            }
        },
    },
    watch: {
        orgCode: {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.getList()
            },
            deep: true
        },
        warehouseCode: {
            handler (newValue) {
                this.listQuery.warehouseCode = newValue
                this.getList()
            },
            deep: true
        },

        stockType: {
            handler (newValue) {
                this.listQuery.stockType = newValue
                this.getList()
            },
            deep: true
        }
    }
}
