import *  as api from '@/api/project/eas/er/equipmentMaintenance'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'twoOne',
    components: { Pagination },
    props: ['workOrderNumber'],
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                workOrderNumber: undefined
            },
            tableKey: [0],
        }
    },
    created () {
    },
    watch: {
        dt_maintain_person_type () {
            if (this.list && this.list.length) {
                let options = [this.dt_maintain_person_type]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['maintainType']
                )
            }
        }

    },
    computed: {
        // 设备类别字典
        ...mapState({
            dt_maintain_person_type: state => state.eamDict.dt_maintain_person_type,
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('eamDict/getDicData', ['dt_maintain_person_type'])
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        getList () {
            this.listQuery.workOrderNumber = this.workOrderNumber
            api.personRecords(this.listQuery).then(res => {
                this.list = res.data.list
                let options = [this.dt_maintain_person_type]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['maintainType']
                )
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
    }
}
