import * as api from "@/api/project/srm/procurementDemand/divisionRules"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    components: { caiGou, dialogPersonSelect },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            dialogVisible: false,
            temp: {
                rowId: undefined
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pbb: state => state.dict.dt_srm_pbb,
            dt_srm_pbc: state => state.dict.dt_srm_pbc,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pbb", 'dt_srm_pbc'])
    },
    methods: {
        //人员选择
        handlePersonCode () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            this.$set(this.temp, 'assignee', val[0].employeeCode)
            this.$set(this.temp, 'assigneeName', val[0].employeeName)
        },
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //新增弹框确定事件
        createData: throttle(function () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('refresh')
                    })
                } else {
                    return false
                }
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑弹框确定事件
        updateData: throttle(function () {
            api.updateRecord(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('refresh')
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                divisionType: undefined,
                ruleType: undefined,
                assigneeName: undefined,
                represent: undefined
            }
        }
    }
}
