import * as api from '@/api/project/srm/supplierManage/supplierConfigure'
import Pagination from '@/components/Pagination'
import addMain from './dialog/addMain'
import addSub from './dialog/addSub'
import { mapState } from "vuex"
import throttle from 'lodash/throttle'

export default {
    name: 'supplierConfigure',
    components: { Pagination, addMain, addSub },
    data () {
        return {
            list: [],
            listSub: null,
            dataList: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            selectCode: [],
            dialogFormVisible: false,
            dialogVisibleSub: false,
            dialogStatus: '',
            tableKey: [0, 1, 2],
        }
    },

    computed: {
        ...mapState({
            dt_srm_p0: state => state.dict.dt_srm_p0,
            dt_srm_par: state => state.dict.dt_srm_par
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ['dt_srm_p0', 'dt_srm_par'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        // this.getList()

    },
    watch: {
        'dt_srm_par' () {
            this.getList()
        },

    },
    methods: {
        getFreshList () {
            this.getList()
        },
        getFreshSubList () {
            this.getSubList()
        },
        //查询主表
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_par, this.dt_srm_p0]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['changeMode', 'state'])
                this.list = res.data.list
                this.total = res.data.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        //查询明细
        getSubList () {
            api.queryRecordItem(this.selectlistRow.rowId).then(res => {
                this.listSub = res.data.map(v => {
                    v.columnName = v.fieldCode
                    v.columnComment = v.fieldName
                    return v
                })
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        checked () {
            this.$refs.tb.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            //新增
            this.$refs.addMain.resetTemp()
            this.$refs.addMain.dialogStatus = 'create'
            this.$refs.addMain.dialogVisible = true
        },
        handleEffect: throttle(function () {
            if (this.selectlistRow) {
                let dto = {
                    method: 'effect'
                }
                api.effect(this.selectlistRow.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '生效成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleCancel: throttle(function () {
            if (this.selectlistRow) {
                let dto = {
                    method: 'invalid'
                }
                api.effect(this.selectlistRow.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '注销成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate (index, row) {
            //编辑弹窗
            this.$refs.addMain.dialogStatus = 'update'
            this.$refs.addMain.dialogVisible = true
            this.$refs.addMain.temp = { ...row }
            this.$refs.addMain.rowId = row.rowId
        },
        handleDelete (index, row) {
            //删除列表
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecord(row.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        handleCreateSub () {
            if (this.selectlistRow) {
                this.$refs.addSub.dialogVisible = true
                this.$refs.addSub.rowId = this.selectlistRow.rowId
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }

        },
        handleDeleteSub (index, row) {
            //删除列表
            this.$confirm("是否删除该信息", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                api.deleteRecordItem(row.configId, row.rowId).then(() => {
                    this.$message({
                        title: '成功',
                        message: '删除成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getSubList()
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })

        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
}
