import * as api from '@/api/project/ems/indexManagement/processEnergy.js'
import Pagination from '@/components/Pagination'
import caigou from '@/components/Select/caigou'
import { mapState } from 'vuex'
import throttle from 'lodash/throttle'

export default {
    name: 'processEnergy',
    components: { Pagination, caigou },
    data () {
        return {
            list: null,
            // Org: null,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                organizationCode: undefined,
                stepCode: undefined
            },
            temp: {
                stepCode: '',
                organizationCode: '',
                gasConsume: '',
                eleConsume: '',
                waterConsume: '',
                quarter: '',
                remark: ''
            },
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            total: 0,
            theight: 0, //表格高度
            selectlistRow: null,
            rules: {
                stepCode: [
                    {
                        required: true,
                        message: '请选择工序',
                        trigger: 'blur'
                    }
                ],
                organizationCode: [
                    {
                        required: true,
                        message: '请选择工厂',
                        trigger: 'blur'
                    }
                ],
                waterConsume: [
                    {
                        required: true,
                        message: '请输入水消耗',
                        trigger: 'blur'
                    }
                ],
                eleConsume: [
                    {
                        required: true,
                        message: '请输入电消耗',
                        trigger: 'blur'
                    }
                ],
                gasConsume: [
                    {
                        required: true,
                        message: '请输入气电消耗',
                        trigger: 'blur'
                    }
                ],
                quarter: [
                    {
                        required: true,
                        message: '请选择季度',
                        trigger: 'blur'
                    }
                ],
                remark: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_gx_setp_list: state => state.businessComponent.dt_gx_setp_list,
            dt_quarter: state => state.emsDict.dt_quarter
        })
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_gx_setp_list'
        ])
        this.getList()
    },
    destroyed () {
        this.setTableHeight()
        this.$store.dispatch('emsDict/getDicData', ['dt_quarter'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', [
            'dt_gx_setp_list'
        ])
        this.getList()
    },
    methods: {
        getName (name) {
            this.temp.organizationName = name
        },
        getList () {
            api.queryProcess(this.listQuery).then(res => {
                res.data.list.map((v, i) => {
                    v.serial = i + 1
                })
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        // getOrg () {
        //     api.queryOrg().then(res => {
        //         this.Org = res.data
        //     })
        // },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create: throttle(
            function () {
                this.$refs.temp.validate(valid => {
                    if (valid) {
                        api.createProcess(this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleUpdate () {
            //编辑弹窗
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                this.temp = JSON.parse(JSON.stringify(this.selectlistRow[0])) // copy obj
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update: throttle(
            function () {
                //编辑
                this.$refs['temp'].validate(valid => {
                    if (valid) {
                        api.updateProcess(this.temp.rowId, this.temp).then(
                            () => {
                                this.getList()
                                this.dialogFormVisible = false
                                this.$message({
                                    title: '成功',
                                    message: '更新成功',
                                    type: 'success',
                                    duration: 2000
                                })
                            }
                        )
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        handleDelete () {
            //删除列表
            if (this.selectlistRow && this.selectlistRow.length >= 1) {
                this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let ids = []
                        this.selectlistRow.forEach((val, index) => {
                            ids[index] = val.rowId
                        })
                        api.deleteProcess(ids).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        deleteQuery () {
            // 清除查询条件
            this.listQuery.currentPage = 1
            this.listQuery.organizationCode = undefined
            this.listQuery.stepCode = undefined
            this.getList()
        },
        resetTemp () {
            this.temp = {}
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        setTableHeight () {
            //表格高度计算
            this.theight = this.$myFun.getSingleTbHeight()
        },
        selectRow (val) {
            // 获取表格选中时的数据
            this.selectlistRow = val
        }
    }
}
