import * as api from '@/api/project/mes/planManagement/issuedByDispatchTask'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'

export default {
    name: 'issuedByDispatchTask',
    components: { Pagination },
    data() {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                workOrderCode: undefined,
                productCode: undefined,
                queryStateParam: undefined
            },
            queryStateParam: undefined,
            selectlistRow: undefined,
            tableKey: 0
        }
    },
    watch: {
        queryStateParam(value) {
            if (!value) {
                delete this.listQuery.queryStateParam
            } else {
                switch (value) {
                    case 'work_order10':
                        this.listQuery.queryStateParam = 'CREATE'
                        break
                    case 'work_order20':
                        this.listQuery.queryStateParam = 'START'
                        break
                    case 'work_order30':
                        this.listQuery.queryStateParam = 'START'
                        break
                    case 'work_order40':
                        this.listQuery.queryStateParam = 'PAUSE'
                        break
                    case 'work_order50':
                        this.listQuery.queryStateParam = 'COMPLETED'
                        break
                }
            }
        }
    },
    created() {
        this.getList()
    },
    computed: {
        ...mapState({
            dt_work_order_stat: state => state.mesDict.dt_work_order_stat
        })
    },
    mounted() {
        this.$store.dispatch('mesDict/getDicData', ['dt_work_order_stat'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery() {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 获取表格选中时的数据
        selectRow(val) {
            this.selectlistRow = val
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val[val.length - 1])
            }
        },
        rowClickFrom(val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        changeStatus(status) {
            if (Array.isArray(this.selectlistRow) && this.selectlistRow[0]) {
                api.changeStatus(
                    {
                        method: status,
                        qualifiedNumber: 0,
                        scrapNumber: 0,
                        unCompleteNumber: 0
                    },
                    this.selectlistRow[0].workOrderCode
                ).then(res => {
                    if (res.status == 201) {
                        this.$message({
                            title: '成功',
                            message: '修改·成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    }
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择订单',
                    type: 'warning'
                })
            }
        }
    }
}
