import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
export default {
    name: "quotaQuery",
    components: { Pagination, caiGou },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                radio: 0
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0, //表格索引
            rangeDate: null
        }
    },
    computed: {
        ...mapState({
            // dt_srm_p0: state => state.dict.dt_srm_p0,
            // dt_srm_pdr: state => state.dict.dt_srm_pdr,
            // dt_srm_pdt: state => state.dict.dt_srm_pdt,
            // dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.requisitionsTimeStart = value[0]
                this.listQuery.requisitionsTimeEnd = value[1]
            } else {
                this.listQuery.requisitionsTimeStart = undefined
                this.listQuery.requisitionsTimeEnd = undefined
            }
        }
    },
    mounted () {
        // this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            this.list = [
                { params0: '1', params1: 'XX采购组织', params2: '', params3: 'WL001', params4: '测试物料', params5: '2019-01-01', params6: '2019-05-31', params7: 'G001', params8: '测试供应商1', params9: '30%' },
                { params0: '2', params1: 'XX采购组织', params2: '', params3: 'WL001', params4: '测试物料', params5: '2019-01-01', params6: '2019-05-31', params7: 'G002', params8: '测试供应商2', params9: '70%' }
            ]
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
