import * as api from "@/api/project/srm/quotaManagement/quotaSettings"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import ImportFile from "./importFile"
export default {
    name: "quotaQuery",
    components: { Pagination, caiGou, ImportFile },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                orgCode: 'dtcg0001',
                page: true,
                currentPage: 1,
                pageSize: 15,
            },
            Edit: true,
            isCurrentValid: 1,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0, //表格索引
            rangeDate: null,
            importDialogVisible: false,
            modalNo: 'EXTP-BAS-0022'  // 导入模板编号
        }
    },
    computed: {
        ...mapState({
            // dt_srm_p0: state => state.dict.dt_srm_p0,
            // dt_srm_pdr: state => state.dict.dt_srm_pdr,
            // dt_srm_pdt: state => state.dict.dt_srm_pdt,
            // dt_srm_pdu: state => state.dict.dt_srm_pdu,
        })
    },
    watch: {
        'rangeDate' (value) {
            if (value) {
                this.listQuery.firstExecuteTime = value[0]
                this.listQuery.endExecuteTime = value[1]
            } else {
                this.listQuery.firstExecuteTime = undefined
                this.listQuery.endExecuteTime = undefined
            }
        },
        'isCurrentValid' (val) {
            if (val == 1) {
                this.Edit = true
                this.rangeDate = null
                this.listQuery.validTimeStart = undefined
                this.listQuery.validTimeEnd = undefined
            } else {
                this.Edit = false
            }
        }
    },
    mounted () {
        // this.$store.dispatch("dict/getDicData", ["dt_srm_p0", 'dt_srm_pdr', 'dt_srm_pdt', 'dt_srm_pdu'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            if (this.radio == 1) {
                this.listQuery.isCurrentValid = this.isCurrentValid
            } else {
                delete this.listQuery.isCurrentValid
            }
            api.queryRecord(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        // 导入
        handleImport () {
            this.importDialogVisible = true
            this.$nextTick(() => {
                this.$refs.importDialog.listQuery = this.listQuery
                // 传递模板下载文件名称
                this.$refs.importDialog.excelName = '采购配额.xlsx'
                this.$refs.importDialog.getList()
            })
        },
        // 导入弹框右上角关闭
        handleClose () {
            this.importDialogVisible = false
            // 清空已选文件列表
            this.$refs.importDialog.fileList = []
            this.getList()
        },
        // 导出
        handleExport () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = ['序号', '采购组织', '分组号', '物料编码', '物料名称', '有效开始', '有效结束', '供应商编码', '供应商名称', '比例']
                const filterVal = ['rowId', 'orgName', 'groupNo', 'materielCode', 'materielName', 'validTimeStart', 'validTimeEnd', 'supplierCode', 'supplierName', 'rate']
                const data = this.formatJson(filterVal, this.list)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: '采购配额'
                })
                this.downloadLoading = true
            })
        }
    }
};
