import changeModuleSelect from '@/components/template/changeMoudleSelect'

import Pagination from "@/components/Pagination"
export default {
    name: "zdmb",
    components: { changeModuleSelect, Pagination },
    data () {
        return {
            modalnum: null,//模板编号，非模板页面可删
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableData: null,
            tableDataInner: [],
            total: 0,
            listQuery: {
                currentPage: 0,
                pageSize: 15,
            }
        }
    },
    created () { },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //模板切换事件，非模板页面可删
        changeMoudle (val) {
            this.$emit('changeSelect', val)
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            this.tableData = [{
                id: '0',
                name: '第一张专辑',
                category: '专辑',
                desc: '第一张创作专辑',
                address: '相信音乐',
                shop: '相信音乐国际股份有限公司'
            }, {
                id: '1',
                name: '第二张专辑',
                category: '专辑',
                desc: '爱情万岁',
                address: '相信音乐',
                shop: '相信音乐国际股份有限公司'
            }, {
                id: '2',
                name: '第三张专辑',
                category: '专辑',
                desc: '人生海海',
                address: '相信音乐',
                shop: '相信音乐国际股份有限公司'
            }, {
                id: '3',
                name: '第四张专辑',
                category: '专辑',
                desc: '时光机',
                address: '相信音乐',
                shop: '相信音乐国际股份有限公司'
            }, {
                id: '4',
                name: '第五张专辑',
                category: '专辑',
                desc: '神的孩子都在跳舞',
                address: '相信音乐',
                shop: '相信音乐国际股份有限公司'
            }
            ]
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            //新增按钮单击事件方法
        },
        handleUpdate () {
            //编辑按钮单击事件方法
        },
        handleDelete () {
            //删除按钮单击事件方法
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            if (this.isSingle) {
                this.$refs.tb.clearSelection() //清除其他行的选中
            }
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件
        },
        expandChange (row) {
            this.tableDataInner[row.id] = [{
                id: '1',
                name: '陈信宏',
                category: 'mayday',
                desc: '到前面来' + row.id,
                address: '福建省泉州市',
                shop: '相信音乐' + row.id,
                shopId: '1'
            }, {
                id: '2',
                name: '温尚翊',
                category: 'mayday',
                desc: '到前面来' + row.id,
                address: '台湾省台北市',
                shop: '相信音乐' + row.id,
                shopId: '2'
            }]
        }
    }
};
