result:
.fl {
    float: left;
}
.fr {
    border-left: 1px solid #d8dce5;
    float: right;
}

.arrow_scroll {
    cursor: pointer;
    width: 31px;
    height: 32px;
    line-height: 32px;
    background-color: #fff;
    border-bottom: 1px solid #d8dce5;
    padding: 0 8px;
}

.arrow_hover:hover {
    background-color: #d8dce5;
}

#tagdiv::after,
#tagdiv::before {
    display: table;
}

#tagdiv::after,
#tagdiv::before {
    content: '';
}
.tags-view-container {
    float: left;
    text-align: left;
    height: 32px;
    //   width: 100%;
    width: calc(100% - 62px);
    background: #fff;
    border-bottom: 1px solid #d8dce5;
    // 取消阴影
    // box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .12), 0 0 3px 0 rgba(0, 0, 0, .04);
    .tags-view-wrapper {
        .tags-view-item {
            display: inline-block;
            position: relative;
            cursor: pointer;
            height: 32px;
            line-height: 32px;
            border: 1px solid #d8dce5;
            color: #495060;
            background: #fff;
            padding: 0 8px;
            font-size: 12px;
            //   margin-left: 5px;
            //   margin-top: 3px;
            //   &:first-of-type {
            //     margin-left: 15px;
            //   }
            //   &:last-of-type {
            //     margin-right: 15px;
            //   }
            &.active {
                background-color: #1a5f94;
                color: #fff;
                border-color: #1a5f94;
                // &::before {
                //     content: '';
                //     background: #fff;
                //     display: inline-block;
                //     width: 8px;
                //     height: 8px;
                //     border-radius: 50%;
                //     position: relative;
                //     margin-right: 2px;
                // }
            }
        }
    }
    .contextmenu {
        margin: 0;
        background: #fff;
        z-index: 3000;
        position: absolute;
        list-style-type: none;
        padding: 5px 0;
        border-radius: 4px;
        font-size: 12px;
        font-weight: 400;
        color: #333;
        box-shadow: 2px 2px 3px 0 rgba(0, 0, 0, 0.3);
        li {
            margin: 0;
            padding: 7px 16px;
            cursor: pointer;
            &:hover {
                background: #eee;
            }
        }
    }
}
