import Pagination from "@/components/Pagination"; // 分页组件
import * as api from "@/api/project/wms/writeOff/certificatecancel";
import { DT_ORG_TYPE } from "@/utils/constant";
import orgSelect from "@/components/Select/orgSelect.vue";
import khxz from "@/components/Select/khxz"; //供应商选择
import AuthoType from "@/components/Select/AuthoType.vue"; //权限类型下拉组件
import * as api1 from "@/api/project/wms/productionMaterial/pullerStorage";
import { mapState } from "vuex";
import warehouseSelect from "@/components/Select/warehouse-select.vue"; //下拉组件
export default {
    name: "proofQuery",
    components: { Pagination, orgSelect, khxz, AuthoType, warehouseSelect },
    data () {
        return {
            list: null,
            list1: null,
            list2: null,
            list3: null,
            areaList: null,
            locatorList: [],
            toareaList: null,
            tolocatorList: [],
            value1: "",
            value2: "",
            value3: "",
            valuedate: "",
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            activeName2: "first",
            theight: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                pushTimeStart: undefined,
                executionStatus: undefined,
                orgCode: undefined,
                warehouseCode: undefined,
                method: undefined,
                toOrgCode: undefined,
                accountDate: this.timeFormat(),
                receiptType: 1,
                reversalState: "0"
            },
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuery1: {
                currentPage: 1,
                pageSize: 10
            },
            listQuery2: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                executionStatus: undefined,
                pushOrderCode: undefined
            },
            dta: {
                accountDate: undefined,
                reversalReceiptDetails: [],
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                reversalReceiptDetails: [],
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            isEdit: true,
            locateWarehouseCode: null,
            DT_ORG_TYPE: DT_ORG_TYPE,
            tableKey: [0, 1],
            downloadLoading: false,
            dialogPrintVisible: false,
            printData: {},//打印数据
        };
    },

    created () {
    },
    computed: {
        ...mapState({
            dt_work_center_workshop: state =>
                state.businessComponent.dt_work_center_workshop,
            dt_cx_type_screenGroupNum_rkpz: state =>
                state.businessComponent.dt_cx_type_screenGroupNum_rkpz
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", [
            "dt_materiel_type",
            "dt_materiel_classify"
        ]);
        this.$store.dispatch("businessComponent/getBusinessComponentData", ["dt_cx_type_screenGroupNum_rkpz"]);
        this.setTableHeight()

    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 195;
        },
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getList2();
            } else {
                // this.getList();
            }
        },
        handleQuery () {
            this.listQuery.currentPage = 1;
            if (this.listQuery.orgCode && this.listQuery.warehouseCode && this.listQuery.businessType) {
                this.getList();
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择组织,仓库,业务类型",
                    type: "warning"
                });
            }

        },
        handleQuery1 () {
            this.listQueryProofMain.currentPage = 1;
            this.getList2();
        },
        timeFormat () {
            var d = new Date();
            var year = d.getFullYear();       //年  
            var month = d.getMonth() + 1;     //月  
            var day = d.getDate();            //日  

            var clock = year + "-";
            if (month < 10)
                clock += "0";
            clock += month + "-";

            if (day < 10)
                clock += "0";
            clock += day + " ";
            return (clock);
        },
        getList () { //查询出库登记主表
            this.list1 = []
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list;
                this.total = res.data.pages.count;
                this.$nextTick(function () {
                    this.checked()
                })
            });

        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true);//官网方法，重点
        },
        getList1 () {//查询出库登记从表

            this.listQueryItems.sourceReceiptCode = this.selectlistRow.sourceReceiptCode;
            this.listQueryItems.businessType = this.selectlistRow.businessType;
            api.queryRecordmx(this.listQueryItems).then(res => {
                this.list1 = res.data.list;
                this.total1 = res.data.pages.count;
                for (let i = 0; i < this.list1.length; i++) {
                    this.locatorList.push([]);
                }
                api1.getArea(this.locateWarehouseCode).then(resa => {
                    this.areaList = resa.data;
                    this.toareaList = resa.data;
                });
            });
        },

        getList2 () {//查询出库凭证主表
            this.listQuery2.businessType = '0526'
            if (this.value3) {
                var d1 = this.value3[0];
                var d2 = this.value3[1];
                this.listQueryProofMain.createDateStart = d1;
                this.listQueryProofMain.createDateEnd = d2;
            } else {
                this.listQueryProofMain.createDateStart = "";
                this.listQueryProofMain.createDateEnd = "";
            }
            this.list3 = []
            api.queryDetails(this.listQueryProofMain).then(res => {
                this.list2 = res.data.list;
                this.total2 = res.data.pages.count
            });
        },

        getList3 () {//查询出库凭证从表
            this.listQueryProofSub.reversalReceiptCode = this.selectlistRowout.reversalReceiptCode;
            api.queryitems(this.listQueryProofSub).then(res => {
                this.list3 = res.data.list;
                this.total3 = res.data.pages.count;
            });
        },

        //冲销
        handleSave () {
            if (this.listQuery.businessType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow) {
                    let a = this.cxmain()
                    if (a) {
                        this.dta = this.selectlistRow
                        this.dta.businessType = this.listQuery.businessType;
                        this.dta.accountDate = this.listQuery.accountDate;
                        this.dta.sourceReceiptCode = this.selectlistRow.sourceReceiptCode
                        this.dta.customerCode = '*'
                        this.dta.reversalReceiptDetails = a;
                        api.cancel(this.dta).then(() => {
                            this.$notify({
                                title: "成功",
                                message: "保存成功",
                                type: "success",
                                duration: 2000
                            });
                            this.getList1()
                        });
                    }

                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    });
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                });
            }
        },
        cxmain () { //冲销状态校验
            let result = true
            this.list1.forEach(val => {
                if (val.reversalState == 1) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "已冲销不能冲销",
                        type: "warning"
                    });
                    result = false;
                }
            })
            if (result) {
                return this.list1
            } else {
                return false;
            }
        },
        cxsub () { //冲销状态校验
            let result = true
            this.dto.reversalReceiptDetails = this.selectlistRow3
            this.dto.reversalReceiptDetails.forEach(val => {
                if (val.reversalState == 1) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "已冲销不能冲销",
                        type: "warning"
                    });
                    result = false;
                }
            })
            if (result) {
                return this.dto.reversalReceiptDetails
            } else {
                return false;
            }
        },
        //复制
        handleCopy () {
            if (this.selectlistRow3) {
                this.dto = this.selectlistRow
                let a = this.cxsub()
                if (a) {
                    this.dto.reversalReceiptDetails = a
                    api.cancel(this.dto).then(() => {
                        this.$notify({
                            title: "成功",
                            message: "冲销成功",
                            type: "success",
                            duration: 2000
                        });
                        this.getList1()
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                });
            }
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowout) {
                this.dialogPrintVisible = true;
                this.dialogStatus = "print";
                this.printData = this.selectlistRowout;
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.reversalReceiptCode)
                });

            } else {
                this.$message.warning(this.$t('message.checkedplease'));
                return false;
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("");
            var newstr = document.getElementById('receipt').innerHTML;
            newWin.document.write(newstr);
            newWin.document.close();//IE添加

            setTimeout(function () {
                newWin.print();
                newWin.close();
            }, 100);
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '打印次数', '冲销凭证号', '凭证号',
                    '凭证行号', '业务类型', '物料编码',
                    '物料名称', '数量', '组织编码', '往来单位',
                    '往来库区', '往来库位', '仓库编码',
                    '库区', '库位', '记账日期',
                    '创建日期', '记账人', '凭证行', '指示号', '指示行'
                ]
                const filterVal = [
                    'printNumber', 'reversalReceiptCode', 'batchNo',
                    'reversalRowNo', 'businessTypeName', 'materielCode',
                    'materielName', 'reversalNum', 'orgCode', 'toOrgCode',
                    'toWareAreaCode', 'toWareLocationCode',
                    'accountDate', 'createTime',
                    'creator', 'rowId', 'sourceOrderCode', 'sourceDetailNo',
                ]
                const data = this.formatJson(filterVal, this.list3)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.locatorList, i, res);
                this.$set(this.list1[i], 'wareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            });
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.locateWarehouseCode, areaCode, i);
        },
        tochangeAreaSel (areaCode, i) {
            this.togetLocatorList(this.locateWarehouseCode, areaCode, i);
        },
        togetLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                this.$set(this.tolocatorList, i, res);
                this.$set(this.list1[i], 'toWareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            });
        },
        update1 () {//客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQuery.customerCode = this.parentsRows[0].customerCode;
                this.khxzDialogFormVisible = false;
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        },
        update2 () { //客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQueryProofMain.customerCode = this.parentsRows[0].customerCode;
                this.khxzDialogFormVisible1 = false;
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                });
            }
        },
        handleDblclickgysxz (row) {
            //父子页面通信
            this.parentsRows = row;
            // this.gysxzDialogFormVisible = false;
        },
        handleClickgysxz () {
            this.khxzDialogFormVisible = true;
        },
        handleClickgysxz1 () {
            this.khxzDialogFormVisible1 = true;
        },
        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.dd.clearSelection();
                this.$refs.dd.toggleRowSelection(val.pop());
                this.$refs.tb.setCurrentRow(this.selectlistRow);
            }

            if (this.selectlistRow) {
                this.locateWarehouseCode = this.selectlistRow.warehouseCode
                this.getList1();
            }
        },
        selectRow2 (val) { //出库登记单击行选中方法
            this.selectlistRow2 = val;
            this.locateWarehouseCode = val.warehouseCode;
            this.$refs.dd.clearSelection();
            this.$refs.dd.toggleRowSelection(val);
        },

        selectRow3 (val) { //已选项选中行
            this.selectlistRow3 = val;
        },
        selectRowout (val) {//出库凭证复选框选中方法
            this.selectlistRowout = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.tb.clearSelection();
                this.$refs.tb.toggleRowSelection(val.pop());
            }
            this.$refs.tb.setCurrentRow(this.selectlistRowout);
            if (this.selectlistRowout) {
                this.getList3();
            }
        },
        selectRowout1 (val) {//出库凭证单击行选中方法
            this.selectlistRowout1 = val;
            this.$refs.tb.clearSelection();
            this.$refs.tb.toggleRowSelection(val);
        },
        selectRowchange (val) {
            // this.selectlistRowchange = val;
            this.selectlistRowchange = val[val.length - 1];
            if (val.length > 1) {
                this.$refs.tbb.clearSelection();
                this.$refs.tbb.toggleRowSelection(val.pop());
            }
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val;
            } else if (i === 3) {
                this.listQuery.warehouseCode = val;
                // this.listQuery.businessType = this.dt_cx_type_screenGroupNum_wzsdb.options[0].wareRuleCode;
                // this.getList();
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val;
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val;
                // this.listQueryProofMain.businessType = this.dt_cx_type_screenGroupNum_wzsdb.options[0].wareRuleCode;
                this.getList2();
            }
        },
    }
};
