import * as api from '@/api/project/eas/database/supplierMain'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex'
export default {
    name: 'supplierMain',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                supplierCode: undefined,
                supplierName: undefined,
                supplierType: undefined
            },
            temp: {
                supplierCode: undefined,
                supplierName: undefined,
                supplierType: undefined,
                supplyClassify: undefined,
                supplierGrade: undefined,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            },
            supplierCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogStatus: '',
            tableKey: 0,
            modalNo: 'EXTP-BAS-0001' //导入模板编号
        }
    },
    computed: {
        ...mapState({
            dt_supplier_type: state => state.dict.dt_supplier_type,
            dt_supplier_level: state => state.dict.dt_supplier_level,
            dt_materiel_classify: state => state.dict.dt_materiel_classify
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', [
            'dt_supplier_type',
            'dt_supplier_level',
            'dt_materiel_classify'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },

        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        selectRow (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb_a.clearSelection() //清除其他行的选中
                this.$refs.tb_a.toggleRowSelection(
                    val[val.length - 1],
                    'selected'
                ) //单击行绑定点击事件
            }
            this.$emit('change', this.selectlistRow)
        }
    }
}
