import * as api from '@/api/project/eas/ESC/pointCheckRecord' // 增、删、改api引入
import { DT_ORG_TYPE } from '@/utils/constant'
import Pagination from '@/components/Pagination'
import { mapState } from 'vuex' // 基本数据引入
import caiGou from '@/components/Select/xtpt/caigou' //下拉组件
import employeesSelect from '@/components/Select/employeesSelect' //下拉组件
import faultType from '../../components/faultTypeSelect'
import deviceTypeSelect from '../../components/deviceTypeSelect'
import teamSelect from '../../components/teamSelect'

export default {
    name: 'pointCheckRecord',
    components: {
        Pagination,
        caiGou,
        employeesSelect,
        faultType,
        deviceTypeSelect,
        teamSelect
    },
    data () {
        return {
            isRepair: false, //是否已报修
            checkList: [],
            textarea: '',
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            workCenterData: [],
            device: [],
            selectDevice: [],
            parts: [],
            list: [],
            fromList: [],
            total: 0,
            total2: 0,
            tableKey: [0, 1], //表格索引
            dateRange: undefined,
            listQuery: {
                organizationCode: undefined,
                inspectionDateStart: undefined,
                inspectionDateEnd: undefined,
                //查询
                currentPage: 1,
                pageSize: 10
            },
            listQuery2: {
                deviceCode: undefined,
                deviceName: undefined,
                modelNumber: undefined,
                typeCode: undefined,
                //从表查询
                currentPage: 1,
                pageSize: 10
            },
            temp: {
                // bomId: undefined,
                materielCode: undefined,
                productName: undefined,
                cardinalNumber: undefined,
                version: undefined,
                // status: undefined,
                productionNumber: undefined,
                orderNumber: undefined,
                organizationCode: undefined,
                bomType: undefined,
                bomCategory: undefined,
                bomRank: undefined,
                startDate: undefined,
                endDate: undefined,
                timeRange: undefined
            },
            tempFrom: {
                deviceCode: undefined,
                deviceName: undefined,
                organizationCode: undefined,
                organizationName: undefined,
                mainMaintainerCode: undefined,
                mainMaintainerName: undefined,
                description: undefined,
                classCode: undefined,
                partCode: undefined,
                faultGrade: undefined,
                urgencyDegree: undefined,
                faultTypeCode: undefined,
                mobile: undefined,
                operator: undefined,
                operatorName: undefined,
                happenTime: undefined,
                typeCode: undefined,
                addressName: undefined,
                ifMachineHalt: undefined,
                useDeptName: undefined,
                useDept: undefined
            },
            rules: {
                deviceCode: [
                    {
                        required: true,
                        message: '请选择设备',
                        trigger: 'blur'
                    }
                ],
                organizationCode: [
                    {
                        required: true,
                        message: '组织编码不能为空',
                        trigger: 'blur'
                    }

                ],
                classCode: [
                    {
                        required: true,
                        message: '维修班组不能为空',
                        trigger: 'blur'
                    }
                ],
                faultGrade: [
                    {
                        required: true,
                        message: '故障等级不能为空',
                        trigger: 'blur'
                    }
                ],
                faultTypeCode: [
                    {
                        required: true,
                        message: '故障类型不能为空',
                        trigger: 'blur'
                    }
                ],
                happenTime: [
                    {
                        required: true,
                        message: '发生时间不能为空',
                        trigger: 'blur'
                    }
                ],
                mainMaintainerCode: [
                    {
                        required: true,
                        message: '维修人不能为空',
                        trigger: 'blur'
                    }
                ],
                operator: [
                    {
                        required: true,
                        message: '操作人不能为空',
                        trigger: 'blur'
                    }
                ],
                operatorName: [
                    {
                        required: true,
                        message: '操作人名称不能为空',
                        trigger: 'blur'
                    }
                ],
                partCode: [
                    {
                        required: true,
                        message: '故障部位不能为空',
                        trigger: 'blur'
                    }
                ],
                urgencyDegree: [
                    {
                        required: true,
                        message: '紧急程度不能为空',
                        trigger: 'blur'
                    }
                ],
                mobile: [
                    {
                        required: true,
                        message: '电话不能为空',
                        trigger: 'blur'
                    }
                ],
            },
            DT_ORG_TYPE: DT_ORG_TYPE,
            dialogFormVisible: false,
            fromDialogFormVisible: false,
            employeeDialogVisible: false,
            employeeDialogVisible2: false,
            deviceDialogVisible: false,
            selectedRows: null, //多选时选中的所有行
            selectFromList: null //从表选中
        }
    },
    filters: {
        defaultValFilter: function (e) {
            if (Array.isArray(e)) {
                return e.join(',')
            } else {
                return e
            }
        }
    },
    created () {
        this.getList()
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_bom_rank: state => state.eamDict.dt_bom_rank,
            dt_urgency_degree: state => state.eamDict.dt_urgency_degree,
            dt_fault_grade: state => state.eamDict.dt_fault_grade,
            dt_yes_or_no: state => state.eamDict.dt_yes_or_no
        })
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        'temp.timeRange' (value) {
            if (value) {
                this.temp.inspectionStartTime = value[0]
                this.temp.inspectionEndTime = value[1]
            }
        },
        'dateRange' (value) {
            if (value) {
                this.listQuery.inspectionDateStart = value[0]
                this.listQuery.inspectionDateEnd = value[1]
            } else {
                this.listQuery.inspectionDateStart = undefined
                this.listQuery.inspectionDateEnd = undefined
            }
        }
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', [
            'dt_bom_rank',
            'dt_urgency_degree',
            'dt_fault_grade',
            'dt_yes_or_no'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.queryAddtionData()
    },
    methods: {
        organizationName (name) {
            this.tempFrom.organizationName = name
        },
        getType (value) {
            if (value) {
                api.queryPart(value).then(res => {
                    this.parts = res.data
                })
            } else {
                this.parts = []
            }
        },
        handleEmployee (val) {
            if (val.length == 0) return
            if (this.dialogFormVisible) {
                this.tempFrom.operator = val[0].employeeCode
                this.tempFrom.operatorName = val[0].employeeName
            } else {
                this.listQuery.pointInspector = val[0].employeeCode
            }
        },
        handleEmployee2 (val) {
            if (val.length == 0) return
            this.tempFrom.mainMaintainerCode = val[0].employeeCode
            this.tempFrom.mainMaintainerName = val[0].employeeName
        },
        // 点击确认检验记录
        create () {
            var recordState = true
            this.temp.inspectionTaskRecordItems.forEach(item => {
                if (item.recordMode == 'dt_record_mode_multiple') {
                    if (item.defaultVal.length == 0) {
                        recordState = false
                    }
                }
            })
            if (!recordState) {
                this.$message({
                    title: '警告',
                    message: '请登记多选类型巡检结果',
                    type: 'warning'
                })
                return false
            }
            this.temp.inspectionTaskRecordItems.forEach(item => {
                if (item.recordMode == 'dt_record_mode_multiple') {
                    item.defaultVal = item.defaultVal.join(',')
                    item.normalOption = item.normalOption.join(',')
                    item.exceptionOption = item.exceptionOption.join(',')
                }
            })
            api.patchSingeRecord(
                this.temp.inspectionTaskCode,
                this.temp.inspectionTaskRecordCode,
                this.temp
            ).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '登记成功',
                        type: 'success'
                    })
                }
                this.fromDialogFormVisible = false
                this.getList()
            })
        },
        // 修改点检项对状态的判断
        handleResult () {
            this.temp.inspectionTaskRecordItems.forEach(item => {
                if (item.recordMode == 'dt_record_mode_multiple') {
                    // 多选(如果包含任意一个异常选项则状态为异常)
                    var normalState = true,
                        exceptionState = false
                    item.defaultVal.forEach(value => {
                        item.exceptionOption.forEach(Option => {
                            if (value == Option) {
                                exceptionState = true
                            }
                        })
                        item.normalOption.forEach(Option => {
                            if (value == Option) {
                                normalState = true
                            }
                        })
                    })
                    if (
                        (normalState && exceptionState) ||
                        (!normalState && exceptionState)
                    ) {
                        item.state = 1
                    } else if (normalState && !exceptionState) {
                        item.state = 0
                    }
                } else if (item.recordMode == 'dt_record_mode_single ') {
                    // 单选(选择异常选项则为异常)
                    if (item.defaultVal == item.normalOption) {
                        item.state = 0
                    } else {
                        item.state = 1
                    }
                } else if (item.recordMode == 'dt_record_mode_value') {
                    // 数值(如果不在最大值和最小值之间，则为异常)
                    if (
                        item.defaultVal >= item.lowerVal &&
                        item.defaultVal <= item.upperVal
                    ) {
                        item.state = 0
                    } else {
                        item.state = 1
                    }
                } else if (item.recordMode == 'dt_record_mode_text') {
                    // 文本(用户自主选择是否异常)
                }
            })
        },
        // 获取工位
        queryAddtionData () {
            api.getWorkCenter().then(res => {
                this.workCenterData = res.data
            })
            api.queryDevice(this.listQuery2).then(({ data }) => {
                this.device = data.list
                this.total2 = data.pages.count
            })
        },
        // 行点击检查
        handleCheckRow (row) {
            api.querySingeRecord(
                row.inspectionTaskCode,
                row.inspectionTaskRecordCode
            ).then(res => {
                this.resetTemp()
                this.fromDialogFormVisible = true
                this.temp = res.data
                var tmp = res.data.inspectionTaskRecordItems
                tmp.forEach(item => {
                    if (item.recordMode == 'dt_record_mode_multiple') {
                        item.normalOption = item.normalOption.split(',')
                        item.exceptionOption = item.exceptionOption.split(',')
                        item.defaultVal = item.defaultVal.split(',')
                    } else if (item.recordMode != 'dt_record_mode_single ') {
                        item.normalOption = ''
                        item.exceptionOption = ''
                    }
                })
                this.temp.inspectionTaskRecordItems = tmp
                this.temp.inspectionName = row.inspectionName
                this.temp.deviceName = row.deviceName
                this.temp.inspectionStartTime
                    ? (this.temp.timeRange = [
                        row.inspectionStartTime,
                        row.inspectionEndTime
                    ])
                    : ''
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        getList () {
            //获取表格数据
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        resetTemp () {
            this.temp = {
                // bomId: undefined,
                materielCode: undefined,
                productName: undefined,
                cardinalNumber: undefined,
                version: undefined,
                // status: undefined,
                productionNumber: undefined,
                orderNumber: undefined,
                organizationCode: undefined,
                bomType: undefined,
                bomCategory: undefined,
                bomRank: undefined,
                startDate: undefined,
                endDate: undefined,
                timeRange: undefined
            }
        },
        handleQuery () {
            //表格查询
            this.listQuery.currentPage = 1
            this.getList()
            this.fromList = undefined
        },
        handleQuery2 () {
            this.listQuery2.currentPage = 1
            this.queryAddtionData()
        },
        resetTempFrom () {
            this.tempFrom = {
                deviceCode: undefined,
                deviceName: undefined,
                organizationCode: undefined,
                organizationName: undefined,
                mainMaintainerCode: undefined,
                mainMaintainerName: undefined,
                description: undefined,
                classCode: undefined,
                partCode: undefined,
                faultGrade: undefined,
                urgencyDegree: undefined,
                faultTypeCode: undefined,
                mobile: undefined,
                operator: undefined,
                operatorName: undefined,
                happenTime: undefined,
                typeCode: undefined,
                addressName: undefined,
                ifMachineHalt: undefined,
                useDeptName: undefined,
                useDept: undefined
            }
        },
        handleCreate () {
            //新增按钮单击事件方法
            if (this.selectedRows) {
                api.queryPart(this.selectedRows.typeCode).then(res => {
                    this.parts = res.data
                })
                this.isRepair = this.selectedRows.isRepair ? true : false
                if (this.selectedRows.isRepair) {
                    this.$message({
                        title: '警告',
                        message: '该记录已报修，无法修改',
                        type: 'warning'
                    })
                    this.resetTempFrom()
                    this.dialogFormVisible = true
                    api.queryRepair(this.selectedRows.inspectionTaskRecordCode).then(res => {
                        this.tempFrom = res.data
                        this.transData()
                        this.$forceUpdate()
                        setTimeout(() => {
                            // 故障类型下拉title
                            var str = (JSON.stringify(this.$refs.parentFault.options)).replace(/\[|]/g, '').replace(/,"children":/g, '}').replace(/}}/g, '}').replace(/}{/g, '},{').replace(/}}/g, '}')
                            var errData = JSON.parse('[' + str + ']')
                            setTimeout(() => {
                                var title = errData.filter(item => item.faultCode == this.tempFrom.faultTypeCode)
                                this.$refs.parentFault.valueTitle = title.length ? title[0].faultName : ''
                                // 班组下拉title
                                var name = this.$refs.teamSelect.options.filter(item => item.teamCode == this.tempFrom.teamCode)
                                this.$refs.teamSelect.valueTitle = name.length ? name[0].teamName : ''
                            }, 500);
                        }, 0);
                    })
                } else {
                    this.resetTempFrom()
                    this.dialogFormVisible = true
                    this.tempFrom = this.selectedRows
                    this.$nextTick(() => {
                        this.$refs.parentFault.valueTitle = ''
                        this.transData()
                    })
                }
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        transData () {
            this.tempFrom.inspectionTaskRecordCode = this.selectedRows.inspectionTaskRecordCode
            this.tempFrom.deviceName = this.selectedRows.deviceName
            this.tempFrom.typeCode = this.selectedRows.typeCode
            this.tempFrom.useDept = this.selectedRows.useDept
            this.tempFrom.useDeptName = this.selectedRows.useDeptName
            this.tempFrom.addressName = this.selectedRows.addressName
            this.tempFrom.organizationCode = this.selectedRows.organizationCode
            this.tempFrom.organizationName = this.selectedRows.organizationName
        },
        update () {
            this.$refs.tempFrom.validate(validate => {
                if (validate) {
                    api.addRepair(this.tempFrom.inspectionTaskRecordCode, this.tempFrom).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '报修成功',
                                type: 'success'
                            })
                            this.dialogFormVisible = false
                        }
                    })
                }
            })
        },
        selectChange (val) {
            if (!val.length) {
                this.fromList = []
                return false
            }
            this.selectedRows = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb.setCurrentRow(this.selectedRows)
            if (this.selectedRows) {
                this.getFromList()
            }
        },
        handleClose () {
            this.resetTemp()
        },
        //获取从表数据
        getFromList () {
            api.querySingeRecord(
                this.selectedRows.inspectionTaskCode,
                this.selectedRows.inspectionTaskRecordCode
            ).then(res => {
                var tmp = res.data.inspectionTaskRecordItems
                tmp.forEach(item => {
                    if (item.recordMode == 'dt_record_mode_multiple') {
                        item.normalOption = item.normalOption.split(',')
                        item.exceptionOption = item.exceptionOption.split(',')
                        item.defaultVal = item.defaultVal.split(',')
                    } else if (item.recordMode != 'dt_record_mode_single ') {
                        item.normalOption = ''
                        item.exceptionOption = ''
                    }
                })
                this.fromList = tmp
            })
        },
        //从表选择框选中
        selectFromChange (val) {
            this.selectFromList = val
            if (val.length > 1) {
                this.$refs.tb2.clearSelection()
                this.$refs.tb2.toggleRowSelection(val[val.length - 1])
            }
            this.$refs.tb2.setCurrentRow(this.selectFromList[0])
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        rowClickFrom (val) {
            this.$refs.tb2.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectDevice = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.device.clearSelection()
                this.$refs.device.toggleRowSelection(val.pop())
            }
            this.$refs.device.setCurrentRow(this.selectDevice)
            this.$emit('change', this.selectDevice)
        },
        //主表单行点击方法
        selectRow_line (val) {
            this.$refs.device.clearSelection()
            this.$refs.device.toggleRowSelection(val)
        },
        cancelDevice () {
            this.deviceDialogVisible = false
            this.$refs.device.clearSelection()
            this.tempFrom.deviceName = undefined
            this.tempFrom.useDept = undefined
            this.tempFrom.useDeptName = undefined
            this.tempFrom.address = undefined
            this.tempFrom.addressName = undefined
            this.tempFrom.typeCode = undefined
            this.tempFrom.typeName = undefined
            this.parts = []
        },
        confirmDevice () {
            this.deviceDialogVisible = false
            if (this.selectDevice) {
                this.tempFrom.deviceCode = this.selectDevice.deviceCode
                let target = this.selectDevice
                this.tempFrom.deviceName = target.deviceName
                this.tempFrom.useDept = target.useDept
                this.tempFrom.useDeptName = target.useDeptName
                this.tempFrom.address = target.address
                this.tempFrom.addressName = target.addressName
                this.tempFrom.typeCode = target.typeCode
                this.tempFrom.typeName = target.typeName
                this.getType(target.typeCode)
            }
        }
    }
}
