import * as api from '@/api/project/eas/ESC/definePointInspectionPlan'
import Pagination from '@/components/Pagination'

export default {
    name: 'tab1',
    components: { Pagination },
    data() {
        return {
            list: [],
            detail: [],
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10
            },
            tableKey: [0],
            detailVisible: false
        }
    },

    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        getList() {
            this.listQuery.inspectionPlanCode = this.$parent.$parent.$parent.selectlistRow[0].inspectionPlanCode
            api.queryTask(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },

        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },

        handleRowDetail(row) {
            api.queryTaskDetail(
                row.inspectionTaskCode,
                row.inspectionTaskRecordCode
            ).then(res => {
                this.detailVisible = true
                this.detail = res.data
            })
        }
    }
}
