import * as api from "@/api/project/srm/dataBase/dictMaintenance"
import { mapState } from 'vuex'
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件

export default {
    name: "wlxz",
    components: { caiGou },
    props: ['unMutiple'],
    data () {
        return {
            dialogCodeVisible: false,
            list: null,
            listSelected: [],
            total: 0,
            parentsRows: null,
            treeData: [],
            defaultProps: {
                children: "children",
                label: "name"
            },
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined
            },
            contentStyleObj: {
                height: ""
            },
            selectCode: [],
            tableKey: 0,
            activeName2: 'first',
            DICT_CODE: DICT_CODE,
            parentId: null
        }
    },
    computed: {
        ...mapState({
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_materiel_classify'])
        //表格高度自适应
        window.onresize = () => {

            this.setTreeHeight()
        }
        this.setTreeHeight()
        this.getTree()
        this.getList()
    },
    methods: {
        handleNodeClick (data) {
            //点击树查询
            this.treeGetList(data.code)
        },
        treeGetList (code) {
            //点击树查询
            let dto = {
                currentPage: 1,
                pageSize: 15,
                parentCode: code
            }
            api.queryRecords(dto).then(res => {
                this.list = res.data.list
            })
        },
        //树高度设置
        setTreeHeight () {
            this.contentStyleObj.height = this.$myFun.getSingleTbHeight()
        },
        getTree () {
            api.queryRecords().then(res => {
                res = res.data
                this.treeData = this.$myFun.buildTree(res, 'code', 'parentCode')
            })
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list

            })
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getTree()
            this.getList()
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.materielCode === row.materielCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        confirm () {
            if (this.listSelected && this.listSelected.length > 0) {
                this.$emit('listSelected', this.listSelected)
            } else {
                this.$message({
                    title: "警告",
                    message: "请勾选信息",
                    type: "warning"
                })
            }
        },
        selectMain (val, row) {
            if (this.unMutiple) {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    let arr = []
                    arr.push(val[val.length - 1])
                    this.listSelected = arr
                    if (val.length > 1) {
                        this.$refs.tbselect.clearSelection()
                        this.$refs.tbselect.toggleRowSelection(val[val.length - 1], 'selected')
                    }
                } else {
                    this.listSelected = []
                }
            } else {
                let isuncheck = this.checkUnsel(val, row)
                if (!isuncheck) {
                    if (this.listSelected) {
                        val.forEach((items, indexv) => {
                            let isSelected = false
                            this.listSelected.forEach((item) => {
                                if (item.materielCode === items.materielCode) {
                                    isSelected = true
                                }
                            })
                            if (!isSelected) {
                                this.listSelected.push(val[indexv])
                            }
                        })
                    } else {
                        this.listSelected = val
                    }
                } else {
                    this.listSelected = this.listSelected.filter(item => {
                        return item.materielCode !== row.materielCode
                    })
                }
            }
        }
    }
};
